import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const URI = 'http://localhost:8000/oneCancha/';
const updURI = 'http://localhost:8000/updateCancha/';

const CompEditCancha = () => {
    const navigate = useNavigate();
    const { ID_Cancha } = useParams(); //se guarda como un objeto, por ello los corchetes
    const [Tipo_Cancha, setTipoCancha] = useState('');
    const [Numero_Cancha, setNumeroCancha] = useState('');
    const [Material_Piso, setMaterialPiso] = useState('');
    const [Techado, setTechado] = useState(false);
    const [Con_Gradas, setConGradas] = useState(false);
    const [Estado, setEstado] = useState('');
    const [Capacidad_Jugadores, setCapacidadJugadores] = useState('');
    const status = [
        'Buena Condicion',
        'En Mantenimiento',
        'Sin Mantenimiento'
      ];
    const pisos = [
        'Césped',
        'Tierra',
        'Asfalto'
      ];
    // Procedimiento para actualizar
    const update = async (e) => {
        e.preventDefault();
        // en este caso pasamos la URI más la id, justo como se hizo en las pruebas con thunderclient
        await axios.put(updURI+ID_Cancha, {
            Tipo_Cancha: Tipo_Cancha,
            Numero_Cancha: Numero_Cancha,
            Material_Piso: Material_Piso,
            Techado: Techado,
            Con_Gradas: Con_Gradas,
            Estado: Estado,
            Capacidad_Jugadores: Capacidad_Jugadores,
        });
        navigate('/'); // Regresamos al principio
    };

    const getCanchaById = async () => {

        const res = await axios.get(URI+ID_Cancha);
        try {
            console.log("Respuesta de la api", res.data)
            setTipoCancha(res.data.Tipo_Cancha || '');
            console.log("Tipo_Cancha", res.data.Tipo_Cancha)
            setNumeroCancha(res.data.Numero_Cancha || '');
            setMaterialPiso(res.data.Material_Piso || '');
            setTechado(res.data.Techado || false);
            setConGradas(res.data.Con_Gradas || false);
            setEstado(res.data.Estado || '');
            setCapacidadJugadores(res.data.Capacidad_Jugadores || '');            
        } catch (error) {
            console.log("Error al obtener datos", error);
        }
    };

    useEffect(() => {
        getCanchaById();
    }, []);

    const handleStatusChange = (e) => {
        const selectedOption = e.target.options[e.target.selectedIndex];
        const selectedStatus = selectedOption.innerText;
        setEstado(selectedStatus);
    };

    const handlePisoChange = (e) => {
        const selectedOption = e.target.options[e.target.selectedIndex];
        const selectedPiso = selectedOption.innerText;
        setMaterialPiso(selectedPiso);
    };

    return (
        <div className="formulario">
            <h1>Editar Cancha</h1>
            <form onSubmit={update}>
                <div className="form-group">
                    <label>Tipo Cancha</label>
                    <input type="text" value={Tipo_Cancha} onChange={(e) => setTipoCancha(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Numero_Cancha</label>
                    <input type="text" value={Numero_Cancha} onChange={(e) => setNumeroCancha(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                <label>Material del Piso</label>
                <select value={Material_Piso} onChange={handlePisoChange}>
                    {pisos.map((pi, index) => (
                        <option key={index} value={pi}>{pi}</option>
                    ))}
                </select>
                </div>
                <div className="form-group">
                    <label>Techado</label>
                    <input type="checkbox" checked={Techado} onChange={(e) => setTechado(e.target.checked)} />
                </div>
                <div className="form-group">
                    <label>Con Gradas</label>
                    <input type="checkbox" checked={Con_Gradas} onChange={(e) => setConGradas(e.target.checked)} />
                </div>
                <div className="form-group">
                <label>Estado Actual</label>
                <select value={Estado} onChange={handleStatusChange}>
                    {status.map((stat, index) => (
                        <option key={index} value={stat}>{stat}</option>
                    ))}
                </select>
                </div>
                <div className="form-group">
                    <label>Capacidad_Jugadores</label>
                    <input type="text" value={Capacidad_Jugadores} onChange={(e) => setCapacidadJugadores(e.target.value)} className="form-control" />
                </div>
                <button type="submit" className="btn btn-primary">Actualizar Cancha</button>
            </form>
        </div>
    );
}

export default CompEditCancha;
