/* global google */
import axios from "axios";
import { useState, useEffect, useRef } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const BASE_URI = "http://localhost:8080/api/canchas"; // Base URI para el controlador CanchaController

/* Inicio Funcionamiento de Maps */
const Map = ({ center, zoom, onMapClick, marker }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (!map && mapRef.current && window.google) {
      const newMap = new google.maps.Map(mapRef.current, {
        center,
        zoom,
      });

      newMap.addListener("click", (event) => {
        if (onMapClick) {
          onMapClick(event.latLng);
        }
      });

      setMap(newMap);
    } else if (map) {
      map.setCenter(center);
      map.setZoom(zoom);
    }
  }, [mapRef, map, center, zoom, onMapClick]);

  useEffect(() => {
    if (map && marker) {
      if (map.markers) map.markers.forEach((m) => m.setMap(null));
      const newMarker = new google.maps.Marker({
        position: marker,
        map,
      });
      map.markers = [newMarker];
    }
  }, [map, marker]);

  return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
};
/* Fin Funcionamiento de Maps */

const CompEditCancha = () => {
  const navigate = useNavigate();
  const { ID_Cancha } = useParams();
  const [Tipo_Cancha, setTipoCancha] = useState("Fútbol");
  const [Numero_Cancha, setNumeroCancha] = useState("");
  const [Material_Piso, setMaterialPiso] = useState("Césped");
  const [Techado, setTechado] = useState(false);
  const [Con_Gradas, setConGradas] = useState(false);
  const [Estado, setEstado] = useState("Buena Condición");
  const [Capacidad_Jugadores, setCapacidadJugadores] = useState("");
  const [formErrors, setFormErrors] = useState({});
  const [Coordenadas, setCoordenadas] = useState("");
  const [center, setCenter] = useState({ lat: 19.430154, lng: -99.137414 });
  const [zoom, setZoom] = useState(17);
  const [marker, setMarker] = useState(null);

  const handleMapClick = (latLng) => {
    const newMarker = { lat: latLng.lat(), lng: latLng.lng() };
    setMarker(newMarker);
    setCoordenadas(`${newMarker.lat},${newMarker.lng}`);
    setCenter(newMarker);
    setZoom(18);
  };

  useEffect(() => {
    getCanchaById();
  }, []);

  const getCanchaById = async () => {
    try {
      const res = await axios.get(`${BASE_URI}?idCancha=${ID_Cancha}`);
      const data = res.data;
      setTipoCancha(data.Tipo_Cancha || "");
      setNumero_Cancha(data.Numero_Cancha || "");
      setMaterialPiso(data.Material_Piso || "");
      setTechado(data.Techado || false);
      setConGradas(data.Con_Gradas || false);
      setEstado(data.Estado || "Buena Condición");
      setCapacidadJugadores(data.Capacidad_Jugadores || "");
      setCoordenadas(data.Coordenadas || "");
      const [lat, lng] = data.Coordenadas.split(",").map(parseFloat);
      setMarker({ lat, lng });
      setCenter({ lat, lng });
      setZoom(18);
    } catch (error) {
      Swal.fire("Error", "No se pudo cargar la cancha.", "error");
    }
  };

  const updateCancha = async (e) => {
    e.preventDefault();

    try {
      const data = {
        Tipo_Cancha,
        Numero_Cancha,
        Material_Piso,
        Techado,
        Con_Gradas,
        Estado,
        Capacidad_Jugadores,
        Coordenadas,
      };

      const response = await axios.put(`${BASE_URI}/updateCancha`, data, {
        headers: { "Content-Type": "application/json" },
      });

      if (response.status === 200) {
        Swal.fire("Éxito", "Cancha editada correctamente.", "success");
        navigate(`/showDeportivos/enc`);
      } else {
        Swal.fire("Error", "No se pudo editar la cancha.", "error");
      }
    } catch (error) {
      Swal.fire("Error", "Hubo un problema al editar la cancha.", "error");
    }
  };

  return (
    <div className="formulario">
      <h1>Editar Cancha</h1>
      <form onSubmit={updateCancha}>
        <div>
          <label>Coordenadas (Latitud, Longitud)</label>
          <input type="text" value={Coordenadas} disabled className="form-control" />
        </div>
        <div style={{ height: "400px", width: "100%" }}>
          <Map center={center} zoom={zoom} onMapClick={handleMapClick} marker={marker} />
        </div>
        <div className="form-group">
          <label>Tipo de Cancha</label>
          <select value={Tipo_Cancha} onChange={(e) => setTipoCancha(e.target.value)} className="form-control">
            {["Fútbol", "Básquetbol", "Tenis", "Beisbol"].map((tipo, index) => (
              <option key={index} value={tipo}>
                {tipo}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Numero de Cancha</label>
          <input
            type="text"
            value={Numero_Cancha}
            onChange={(e) => setNumeroCancha(e.target.value)}
            className={`form-control ${formErrors.Numero_Cancha && "is-invalid"}`}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Editar Cancha
        </button>
      </form>
    </div>
  );
};

export default CompEditCancha;
