import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import GoogleMap from './ShowMap';
import Swal from 'sweetalert2';


const URI = 'http://localhost:8000/';

const CompShowDeportivos = () => {
    const [deportivos, setDeportivos] = useState([]);
    useEffect(() => {
        getDeportivoInfo();
    }, []);

    const getDeportivoInfo = async () => {
        const res = await axios.get(`${URI}getDeportivoInfo`);
        setDeportivos(res.data);
    }

    const deleteDeportivoInfo = async (id) => {
        await axios.delete(`${URI}deleteDeportivoInfo/${id}`);
        getDeportivoInfo();
    }

    const deleteCancha = async (ID_Cancha) => {
        await axios.delete(`${URI}deleteCancha/${ID_Cancha}`);
        getDeportivoInfo();
    }

    const [expandedDeportivo, setExpandedDeportivo] = useState(null);
    const [expandedSubAccordion, setExpandedSubAccordion] = useState(null);

    const toggleAccordion = (idDeportivo) => {
        setExpandedDeportivo(idDeportivo === expandedDeportivo ? null : idDeportivo);
    };

    const toggleSubAccordion = (index) => {
        setExpandedSubAccordion(index === expandedSubAccordion ? null : index);
    };

    const handleDeleteDeportivo = (ID_Deportivo) => {
        // Mostrar un mensaje de confirmación utilizando SweetAlert
        Swal.fire({
            title: '¿Estás seguro de eliminar este deportivo?',
            text: 'Esta acción no se puede deshacer',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminar',
            cancelButtonText: 'Cancelar',
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire(
                    '¡Eliminado!',
                    'El deportivo ha sido eliminado exitosamente.',
                    'success',                    
                );
                deleteDeportivoInfo(ID_Deportivo)            
            }
        });
    }

    const handleDelete = (ID_Cancha) => {
        // Mostrar un mensaje de confirmación utilizando SweetAlert
        Swal.fire({
            title: '¿Estás seguro de eliminar esta cancha?',
            text: 'Esta acción no se puede deshacer',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminar',
            cancelButtonText: 'Cancelar',
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire(
                    '¡Eliminado!',
                    'La cancha ha sido eliminado exitosamente.',
                    'success',                    
                );
                deleteCancha(ID_Cancha)            
            }
        });
    };

    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => {
      setShowPassword(!showPassword);
    };

    return (
        <div className="deportivos-accordion">
            <Link to="/createDireccion" className="btn btn-primary mt-2 mtb-2">
                <i className="fa-solid fa-plus"></i>
            </Link>
            {deportivos.map((deportivo) => {
                // Inicializar lat y lng como 0 por defecto
                let lat = 0;
                let lng = 0;

                // Dividir la cadena de coordenadas antes de entrar al bloque JSX
                if (deportivo.direccion && deportivo.direccion.Coordenadas) {
                    [lat, lng] = deportivo.direccion.Coordenadas.split(', ').map(parseFloat);
                }

                return (
                    <div key={deportivo.ID_Deportivo} className="deportivo-card">
                        <div className="accordion-header" onClick={() => toggleAccordion(deportivo.ID_Deportivo)}>
                            <h2>{deportivo.Nombre}</h2>
                            <span>{expandedDeportivo === deportivo.ID_Deportivo ? '-' : '+'}</span>
                        </div>

                        {expandedDeportivo === deportivo.ID_Deportivo && (
                            <div className="accordion-content">
                                <p><strong>Horario General:</strong> {deportivo.Horario_General}</p>
                                <p><strong>Acepta Mascotas:</strong> {deportivo.Acepta_Mascotas ? "Sí" : "No"}</p>

                                <h2>Servicios</h2>
                                <p>
                                    <strong>Tienda:</strong> {deportivo.Tienda ? "Sí" : "No"} 
                                    <strong> - Vestidores:</strong> {deportivo.Vestidores ? "Sí" : "No"}    
                                    <strong> - Regaderas:</strong> {deportivo.Regaderas ? "Sí" : "No"}
                                    <strong> - Médico:</strong> {deportivo.Médico ? "Sí" : "No"}
                                </p>
                                <Link to={`/editDeportivo/${deportivo.ID_Deportivo}`} className='btn btn-info'>
                                    <i className="fa-regular fa-pen-to-square"></i>
                                </Link>
                                <button onClick={() => handleDeleteDeportivo(deportivo.ID_Deportivo)} className='btn btn-danger'>
                                    <i class="fa-solid fa-trash"></i>
                                </button>

                                {deportivo.direccion && (
                                    <div className="sub-accordion">
                                        <h3 onClick={() => toggleSubAccordion(0)}>Dirección</h3>
                                        {expandedSubAccordion === 0 && (
                                            <div className="sub-accordion-content">
                                                <p><strong>Calle:</strong> {deportivo.direccion.Calle}</p>
                                                <p><strong>Número:</strong> {deportivo.direccion.Numero}</p>
                                                <p><strong>Alcaldía:</strong> {deportivo.direccion.Alcaldia}</p>
                                                <p><strong>Código Postal:</strong> {deportivo.direccion.Codigo_Postal}</p>
                                                <p><strong>Referencias:</strong> {deportivo.direccion.Referencias}</p>
                                                <p><strong>Entre Calle 1:</strong> {deportivo.direccion.Entre_Calle_1}</p>
                                                <p><strong>Entre Calle 2:</strong> {deportivo.direccion.Entre_Calle_2}</p>

                                                {/* Mapa de Google Maps */}
                                                <div>
                                                    <GoogleMap
                                                        defaultZoom={15}
                                                        defaultCenter={{ lat, lng }}
                                                        defaultOptions={{ scrollwheel: false }}
                                                        containerElement={<div style={{ height: '400px', width: '100%' }} />}
                                                        mapElement={<div style={{ height: '100%' }} />}
                                                    >
                                                        <Marker position={{ lat, lng }} />
                                                    </GoogleMap>
                                                </div>

                                                {/* Enlaces */}
                                                <Link to={`https://www.google.com/maps/place/${deportivo.direccion.Coordenadas}`} className="btn btn-success" target="_blank">
                                                    <i className="fa-solid fa-map-pin"></i>
                                                </Link>
                                                <Link to={`/editDireccion/${deportivo.direccion.ID_Direccion}`} className="btn btn-info">
                                                    <i className="fa-regular fa-pen-to-square"></i>
                                                </Link>
                                            </div>
                                        )}
                                    </div>
                                )}
                                {/* Otros contenidos de la función */}
                            </div>
                        )}
                    </div>
                );
            })}
        </div>
    );
};
  
export default CompShowDeportivos;
