import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './login.css'; // Importa el archivo CSS

const URI = 'http://localhost:8000/rfcPassword/';

const CompLogin = () => {
    // Estados para RFC, contraseña y mensajes de error
    const [RFC_CURP, setRfc] = useState('');
    const [Contraseña, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    // Hook para navegar a otra ruta después de iniciar sesión
    const navigate = useNavigate();

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };
    
    // Función para manejar el formulario de login
    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            // Realiza una solicitud POST al backend con RFC y contraseña
            const response = await axios.post(URI, { RFC_CURP, Contraseña });

            // Si el inicio de sesión es exitoso (status 200), redirige a /showDeportivos
            if (response.status === 200) {
                Swal.fire(
                    '¡Bienvenido!',
                    RFC_CURP,
                    'success',                    
                );
                navigate('/showDeportivos');
            } else if(response.status === 401) {
                // Si el servidor devuelve un error, muestra un mensaje
                showErrorAlert('Credenciales incorrectas');
            }
        } catch (error) {
            // Manejo de errores
            showErrorAlert('Credenciales incorrectas');
        }
    };

    return (
        <div className="login-container">
            <h2 className="login-title">LOGIN</h2>
            {/* Muestra un mensaje de error si hay */}
            {errorMessage && <div className="error-message">{errorMessage}</div>}
            <form onSubmit={handleLogin} className="login-form">
                <div className="form-group">
                    <label htmlFor="RFC_CURP">RFC:</label>
                    <input
                        type="text"
                        id="RFC_CURP"
                        value={RFC_CURP}
                        onChange={(e) => setRfc(e.target.value)}
                        required
                        className="input-field"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="Contraseña">Contraseña:</label>
                    <input
                        type="password"
                        id="Contraseña"
                        value={Contraseña}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        className="input-field"
                    />
                </div>
                <button type="submit" className="submit-button">Iniciar sesión</button>
            </form>
        </div>
    );
};

export default CompLogin;
