import axios from "axios";
import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const URI = 'http://localhost:8000/createDeportivo/';

const CompCreateDeportivo = () => {
    const navigate = useNavigate();
    const {ID_Direccion} = useParams() 
    const [Nombre, setNombre] = useState('')
    const [Horario_General, setHorarioGeneral] = useState('');
    const [Acepta_Mascotas, setAceptaMascotas] = useState('');
    const [dia1, setDia1] = useState('');
    const [dia2, setDia2] = useState('');
    const [horarioInicio, setHorarioInicio] = useState('');
    const [horarioFin, setHorarioFin] = useState('');
    const [formErrors, setFormErrors] = useState({
        nombre: '',
        horario: '',
        dia1: '',
        dia2: '',
        horarioInicio: '',
        horarioFin: ''
    });
    const diasSemana = [
        'Lunes','Martes','Miercoles','Jueves','Viernes','Sábado','Domingo'
    ]

    const store = async (e) => {
        e.preventDefault();
        
        try {
            const isValid = validateForm();
            if (isValid) {
            const response = await axios.post(URI, { 
                Nombre: Nombre, 
                ID_Direccion: ID_Direccion,
                Horario_General: Horario_General,
                Acepta_Mascotas: Acepta_Mascotas,
            });

            if (response.status === 201) {
                const ID_Deportivo = response.data.ID_Deportivo;
                navigate(`/createEncargado/${ID_Deportivo}`);
            } else {
                showErrorAlert("Hubo un error al crear el deportivo. Inténtalo de nuevo.");
            }
        }
        } catch (error) {
            console.error("Error:", error);
            showErrorAlert("Hubo un error al crear el deportivo. Inténtalo de nuevo.");
        }
    };

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };

    const validateForm = () => {
        let valid = true;
        const errors = {
            nombre: '',
            horario: '',
            dia1: '',
            dia2: '',
            horarioInicio: '',
            horarioFin: ''
        };

        if (!Nombre) {
            errors.nombre = "El nombre es requerido";
            valid = false;
        }

        if (horarioInicio === horarioFin) {
            
            showErrorAlert("El horario de inicio y fin no pueden ser iguales")
            valid = false;
        }

        if (!dia1 || !dia2) {
            errors.dia1 = "Debes seleccionar ambos días";
            errors.dia2 = "Debes seleccionar ambos días";
            valid = false;
        }

        if (!horarioInicio) {
            errors.horarioInicio = "El horario de inicio es requerido";
            valid = false;
        }

        if (!horarioFin) {
            errors.horarioFin = "El horario de fin es requerido";
            valid = false;
        }

        setFormErrors(errors);
        return valid;
    };

    const handleCrearCadena = (e) => {
        e.preventDefault();
        if (dia1.localeCompare(dia2) === 0) {
            const cadena =
                `Solo día ${dia2}` +
                ` de ${horarioInicio} a ${horarioFin}`;
            setHorarioGeneral(cadena);
        } else {
            const cadena =
                `${dia1} a ${dia2}` +
                ` de ${horarioInicio} a ${horarioFin}`;
            setHorarioGeneral(cadena);
        }
    };

    return (
        <div className="formulario">
            <h1>Ingresar nuevo deportivo</h1>
            <h1>Información General</h1>
            <form onSubmit={store}>
                <div className="form-group">
                    <label>Nombre</label>
                    <input type="text" value={Nombre} onChange={(e) => setNombre(e.target.value)} className={`form-control ${formErrors.nombre && 'is-invalid'}`} />
                    {formErrors.nombre && <div className="invalid-feedback">{formErrors.nombre}</div>}
                </div>
                <div className="form-group">
                    <label>Horario General</label>
                    <div>
                        <label>Día 1:</label>
                        <select value={dia1} onChange={(e) => setDia1(e.target.value)} className={`form-control ${formErrors.dia1 && 'is-invalid'}`}>
                            <option value="">Selecciona un día</option>
                            {diasSemana.map((dia, index) => (
                                <option key={index} value={dia}>{dia}</option>
                            ))}
                        </select>
                        {formErrors.dia1 && <div className="invalid-feedback">{formErrors.dia1}</div>}
                    </div>
                    <div>
                        <label>Día 2:</label>
                        <select value={dia2} onChange={(e) => setDia2(e.target.value)} className={`form-control ${formErrors.dia2 && 'is-invalid'}`}>
                            <option value="">Selecciona un día</option>
                            {diasSemana.map((dia, index) => (
                                <option key={index} value={dia}>{dia}</option>
                            ))}
                        </select>
                        {formErrors.dia2 && <div className="invalid-feedback">{formErrors.dia2}</div>}
                    </div>
                    <div>
                        <label>Horario:</label>
                        <input type="time" value={horarioInicio} onChange={(e) => setHorarioInicio(e.target.value)} className={`form-control ${formErrors.horarioInicio && 'is-invalid'}`} />
                        <span> a </span>
                        <input type="time" value={horarioFin} onChange={(e) => setHorarioFin(e.target.value)} className={`form-control ${formErrors.horarioFin && 'is-invalid'}`} />
                        {formErrors.horario && <div className="invalid-feedback">{formErrors.horario}</div>}
                    </div>
                    <input type="text" value={Horario_General} onChange={(e) => setHorarioGeneral(e.target.value)} className="form-control" disabled />
                </div>
                <div className="form-group">
                    <button onClick={handleCrearCadena} className="btn btn-primary">Generar Horario</button>
                    <label>Acepta Mascotas?</label>
                    <input type="checkbox" checked={Acepta_Mascotas} onChange={(e) => setAceptaMascotas(e.target.checked)} />
                </div>
                <button type="submit" className="btn btn-primary">Crear Deportivo</button>
            </form>
        </div>
    );
}

export default CompCreateDeportivo;
