import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const updURI = 'http://localhost:8000/updateHorarios/';
const recURI = 'http://localhost:8000/getHorarioDeportivo/'

const CompEditHorario = () => {
    const navigate = useNavigate();
    const { ID_Deportivo } = useParams();
    const [horarios, setHorarios] = useState([
        { ID_Horario: '', dia_semana: 'Lunes', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { ID_Horario: '', dia_semana: 'Martes', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { ID_Horario: '', dia_semana: 'Miércoles', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { ID_Horario: '', dia_semana: 'Jueves', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { ID_Horario: '', dia_semana: 'Viernes', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { ID_Horario: '', dia_semana: 'Sábado', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { ID_Horario: '', dia_semana: 'Domingo', hora_apertura: '', hora_cierre: '', inhabil: '' }
    ]);

    useEffect(() => {
        const fetchHorarios = async () => {
            try {
                const response = await axios.get(`${recURI}${ID_Deportivo}`);
                const horariosData = response.data;
                
                // Mapear los datos recuperados a la estructura de horarios en el estado
                const updatedHorarios = horarios.map((horario, index) => {
                    const horarioData = horariosData[index] || {}; // Obtener los datos del horario correspondiente
                    return {
                        ID_Horario: ID_Horario,
                        dia_semana: horario.dia_semana,
                        hora_apertura: horarioData.hora_apertura || '',
                        hora_cierre: horarioData.hora_cierre || '',
                        inhabil: horarioData.inhabil || ''
                    };
                });

                setHorarios(updatedHorarios);

            } catch (error) {
                console.error('Error al obtener horarios:', error);
                showErrorAlert('Hubo un error al obtener los horarios del deportivo.');
            }
        };

        fetchHorarios(); // Llamar a la función de recuperación de horarios al cargar el componente
    }, [ID_Deportivo]); // Ejecutar el efecto cuando cambie ID_Deportivo

    const update = async (e) => {
        e.preventDefault();
        try {
            const isValid = horarios.every((horario) =>
                isValidHoras(horario.hora_apertura, horario.hora_cierre, horario.inhabil)
            );
    
            if (!isValid) {
                showErrorAlert("La hora de apertura no puede ser mayor que la de cierre, ni ambas deben ser iguales.");
                return;
            }
    
            const formattedHorarios = horarios.map((horario) => ({
                ID_Horario: ID_Horario,
                ID_Deportivo: ID_Deportivo,
                dia_semana: horario.dia_semana,
                hora_apertura: horario.hora_apertura,
                hora_cierre: horario.hora_cierre,
                inhabil: horario.inhabil
            }));
    
            const postData = { horarios: formattedHorarios };
            await axios.put(updURI, postData);
            navigate(`/showDeportivos/admin/null`);
    
        } catch (error) {
            console.error("Error:", error);
            showErrorAlert("Hubo un error al crear los horarios. Inténtalo de nuevo.");
        }
    };
    

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };

    const handleHorarioChange = (index, field, value) => {
        const updatedHorarios = [...horarios];
        updatedHorarios[index][field] = value;
        setHorarios(updatedHorarios);
    };

    const handleInhabilChange = (index) => {
        const updatedHorarios = [...horarios];
        updatedHorarios[index].inhabil = !updatedHorarios[index].inhabil;
        setHorarios(updatedHorarios);
    };

    const isValidHoras = (hora_apertura, hora_cierre, inhabil) => {
        if (inhabil) {
            // Si está marcado como inhabilitado, no se aplica la validación de horas
            return true;
        }
    
        if (hora_apertura === hora_cierre) {
            // Las horas son iguales pero no está marcado como inhabilitado
            return false;
        }
    
        // Verificar si la hora de apertura es mayor que la hora de cierre
        const aperturaTime = new Date(`2000-01-01T${hora_apertura}`);
        const cierreTime = new Date(`2000-01-01T${hora_cierre}`);
    
        if (aperturaTime >= cierreTime) {
            //showErrorAlert("La hora de apertura no debe ser mayor a la de cierre.");
            return false;  // La hora de apertura es igual o mayor que la hora de cierre        
        }
    
        return true;  // En todos los demás casos, consideramos válido
    };
    

    return (
        <form onSubmit={update}>
            <div className="formulario">
                <h1>Ingresar Horarios del Deportivo</h1>
                {horarios.map((horario, index) => (
                    <div key={index}>
                        <label><h3>{horario.dia_semana}</h3></label>
                        <div>
                            <input
                                type="time"
                                value={horario.hora_apertura}
                                onChange={(e) => handleHorarioChange(index, 'hora_apertura', e.target.value)}
                                className="form-control"
                                placeholder="Hora de apertura"
                                disabled={horario.inhabil} // Deshabilita el input si es inhabil
                            />
                            <span> a </span>
                            <input
                                type="time"
                                value={horario.hora_cierre}
                                onChange={(e) => handleHorarioChange(index, 'hora_cierre', e.target.value)}
                                className="form-control"
                                placeholder="Hora de cierre"
                                disabled={horario.inhabil} // Deshabilita el input si es inhabil
                            />
                            <label>
                                <input
                                    type="checkbox"
                                    checked={horario.inhabil}
                                    onChange={() => handleInhabilChange(index)}
                                />
                                Inhabil
                            </label>
                        </div>
                    </div>
                ))}
                <button type="submit" className="btn btn-primary">Editar Horarios</button>
            </div>
        </form>
    )
}

export default CompEditHorario;
