import axios from "axios";
import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"

const URI ='http://localhost:8000/updateDireccion/'

const navigate = useNavigate()
const {id} = useParams() //se guarda como un objeto, por ello los corchetes

const CompEditDireccion = () => {
    const [Calle, setCalle] = useState('');
    const [Numero, setNumero] = useState('');
    const [Alcaldia, setAlcaldia] = useState('');
    const [Codigo_Postal, setCodigoPostal] = useState('');
    const [Referencias, setReferencias] = useState('');
    const [Entre_Calle_1, setEntreCalle1] = useState('');
    const [Entre_Calle_2, setEntreCalle2] = useState('');

    //Procedimiento para actualizar
    const update = async (e) => {
        e.preventDefault()
        //en este caso pasamos la URI mas la id, justo como se hizo en las pruebas con thunderclient
        await axios.put(URI+id, {
            Calle: Calle, Numero: Numero, Alcaldia: Alcaldia,
                Codigo_Postal: Codigo_Postal, Referencias: Referencias, Entre_Calle_1: Entre_Calle_1,
                Entre_Calle_2: Entre_Calle_2       
            })
            navigate('/')
    }

    return(
            <div className="TEST">
                <h1>Editar cancha existente</h1>
                <h1>Editar cancha existente</h1>
                <h1>Editar cancha existente</h1>
            </div>
        )
}


export default CompEditDireccion
