/* global google */

const Map = ({ center, zoom, onMapClick, marker }) => {
    const mapRef = useRef(null);
    const [map, setMap] = useState(null);
  
    useEffect(() => {
      if (!map && mapRef.current) {
        // Crear un nuevo mapa
        const newMap = new google.maps.Map(mapRef.current, {
          center,
          zoom,
        });
  
        // Agregar el manejador de clic en el mapa
        newMap.addListener("click", (event) => {
          if (onMapClick) {
            onMapClick(event.latLng);
          }
        });
  
        setMap(newMap);
      } else if (map) {
        // Actualizar el centro y el nivel de zoom del mapa existente
        map.setCenter(center);
        console.log(center.lat)
        map.setZoom(zoom);
    }
    }, [mapRef, map, center, zoom, onMapClick]);
  
    useEffect(() => {
      if (map && marker) {
        // Eliminar marcadores existentes
        map.markers && map.markers.forEach((m) => m.setMap(null));
        
        // Crear nuevo marcador
        const newMarker = new google.maps.Marker({
          position: marker,
          map,
        });
        
        map.markers = [newMarker];
      }
    }, [map, marker]);
  
    return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
  };

  export default Map
