/* global google */
import axios from "axios";
import { useState, useEffect, useRef } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const URI = "http://localhost:8000/oneCancha/";
const updURI = "http://localhost:8000/updateCancha/";
const URICNCE = "http://localhost:8000/getCanchaDeportivo"; // Verificar número de cancha existente

const Map = ({ center, zoom, onMapClick, marker }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (!map && mapRef.current && window.google) {
      const newMap = new google.maps.Map(mapRef.current, {
        center,
        zoom,
      });

      newMap.addListener("click", (event) => {
        if (onMapClick) {
          onMapClick(event.latLng);
        }
      });

      setMap(newMap);
    } else if (map) {
      map.setCenter(center);
      map.setZoom(zoom);
    }
  }, [mapRef, map, center, zoom, onMapClick]);

  useEffect(() => {
    if (map && marker) {
      if (map.markers) map.markers.forEach((m) => m.setMap(null));
      const newMarker = new google.maps.Marker({
        position: marker,
        map,
      });
      map.markers = [newMarker];
    }
  }, [map, marker]);

  return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
};

const CompEditCancha = () => {
  const navigate = useNavigate();
  const { ID_Cancha, ID_Deportivo, RFC_CURP } = useParams();
  const [Tipo_Cancha, setTipoCancha] = useState("Fútbol");
  const [Numero_Cancha, setNumeroCancha] = useState("");
  const [Material_Piso, setMaterialPiso] = useState("Césped");
  const [Techado, setTechado] = useState(false);
  const [Con_Gradas, setConGradas] = useState(false);
  const [Estado, setEstado] = useState("Buena Condición");
  const [Capacidad_Jugadores, setCapacidadJugadores] = useState("");
  const [Coordenadas, setCoordenadas] = useState("");
  const [center, setCenter] = useState({ lat: 19.430154, lng: -99.137414 });
  const [zoom, setZoom] = useState(17);
  const [marker, setMarker] = useState(null);

  const handleMapClick = (latLng) => {
    const newMarker = { lat: latLng.lat(), lng: latLng.lng() };
    setMarker(newMarker);
    setCoordenadas(`${newMarker.lat},${newMarker.lng}`);
    setCenter(newMarker);
    setZoom(18);
  };

  const showErrorAlert = (message) => {
    Swal.fire({
      icon: "error",
      title: "Error",
      text: message,
      confirmButtonText: "OK",
    });
  };

  const showSuccessAlert = (message) => {
    Swal.fire({
      icon: "success",
      title: "Éxito",
      text: message,
      confirmButtonText: "OK",
    });
  };

  useEffect(() => {
    const getCanchaById = async () => {
      try {
        const res = await axios.get(`${URI}${ID_Cancha}`);
        const data = res.data;
        setTipoCancha(data.Tipo_Cancha || "Fútbol");
        setNumero_Cancha(data.Numero_Cancha || "");
        setMaterialPiso(data.Material_Piso || "Césped");
        setTechado(data.Techado || false);
        setConGradas(data.Con_Gradas || false);
        setEstado(data.Estado || "Buena Condición");
        setCapacidadJugadores(data.Capacidad_Jugadores || "");
        setCoordenadas(data.Coordenadas || "");
        const [lat, lng] = data.Coordenadas.split(",").map(parseFloat);
        setMarker({ lat, lng });
        setCenter({ lat, lng });
        setZoom(18);
      } catch (error) {
        console.error("Error al obtener la cancha:", error);
        showErrorAlert("No se pudo cargar la cancha.");
      }
    };

    getCanchaById();
  }, [ID_Cancha]);

  const updateCancha = async (e) => {
    e.preventDefault();

    if (!Coordenadas) {
      showErrorAlert("Las coordenadas son obligatorias.");
      return;
    }

    try {
      const datosCancha = {
        idCancha: parseInt(ID_Cancha),
        tipoCancha: Tipo_Cancha,
        numeroCancha: Numero_Cancha,
        materialPiso: Material_Piso,
        techado: Techado,
        conGradas: Con_Gradas,
        estado: Estado,
        capacidadJugadores: Capacidad_Jugadores,
        coordenadas: Coordenadas,
      };

      const response = await axios.put(updURI, datosCancha, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.status === 200) {
        showSuccessAlert("La cancha fue actualizada correctamente.");
        navigate(`/showDeportivos/enc/${RFC_CURP}`);
      } else {
        showErrorAlert("No se pudo actualizar la cancha.");
      }
    } catch (error) {
      console.error("Error al actualizar la cancha:", error);
      showErrorAlert("Ocurrió un error al actualizar la cancha.");
    }
  };

  return (
    <div className="formulario">
      <h1>Editar Cancha</h1>
      <form onSubmit={updateCancha}>
        <div>
          <label>Coordenadas</label>
          <input type="text" value={Coordenadas} disabled className="form-control" />
        </div>
        <div style={{ height: "400px", width: "100%" }}>
          <Map center={center} zoom={zoom} onMapClick={handleMapClick} marker={marker} />
        </div>
        {/* Resto del formulario */}
        <button type="submit" className="btn btn-primary">
          Editar Cancha
        </button>
      </form>
    </div>
  );
};

export default CompEditCancha;
