import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ModalComponent from './ModalComponent'; // Importa el componente del modal

const ComponentWithLinkAndModal = () => {
    const [showModal, setShowModal] = useState(false); // Estado para controlar la visibilidad del modal

    // Función para abrir y cerrar el modal
    const toggleModal = () => {
        setShowModal(!showModal);
    };

    return (
        <div>
            {/* Enlace para abrir el modal */}
            <Link to="#" onClick={toggleModal}>
                Abrir Modal
            </Link>

            {/* Renderizado condicional del modal */}
            {showModal && (
                <ModalComponent onClose={toggleModal}>
                    {/* Contenido del modal */}
                    <h2>Contenido del Modal</h2>
                    <p>Este es el contenido del modal.</p>
                    <button onClick={toggleModal}>Cerrar Modal</button>
                </ModalComponent>
            )}
        </div>
    );
};

export default ComponentWithLinkAndModal;
