import axios from "axios";
import React, { useState, useEffect, useRef } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Wrapper } from "@googlemaps/react-wrapper";
import Swal from "sweetalert2";

const API_KEY = "AIzaSyCiSwQ835LzONcwinoIhq1zE-7keqrGKSs";

const URI = 'http://localhost:8080/api/direcciones/obtenerDireccion'; // URI para obtener información de la dirección a editar
const updURI = 'http://localhost:8080/api/direcciones/updateDireccion'; // URI para actualizar la información de la dirección
const URICDE = 'http://localhost:8080/api/direcciones/obtenerDirecciones'; // URI para comprobar si la dirección ya existe

const Map = ({ center, zoom, onMapClick, marker }) => {
    const mapRef = useRef(null);
    const [map, setMap] = useState(null);

    useEffect(() => {
        if (!map && mapRef.current) {
            const newMap = new google.maps.Map(mapRef.current, {
                center,
                zoom,
            });

            newMap.addListener("click", (event) => {
                if (onMapClick) {
                    onMapClick(event.latLng);
                }
            });

            setMap(newMap);
        } else if (map) {
            map.setCenter(center);
            map.setZoom(zoom);
        }
    }, [mapRef, map, center, zoom, onMapClick]);

    useEffect(() => {
        if (map && marker) {
            map.markers?.forEach((m) => m.setMap(null));

            const newMarker = new google.maps.Marker({
                position: marker,
                map,
            });

            map.markers = [newMarker];
        }
    }, [map, marker]);

    return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
};

const CompEditDireccion = () => {
    const navigate = useNavigate();
    const { ID_Direccion } = useParams();

    const [calle, setCalle] = useState('');
    const [numero, setNumero] = useState('');
    const [alcaldia, setAlcaldia] = useState('Álvaro Obregón');
    const [codigoPostal, setCodigoPostal] = useState('');
    const [coordenadas, setCoordenadas] = useState('');
    const [referencias, setReferencias] = useState('');
    const [entreCalle1, setEntreCalle1] = useState('');
    const [entreCalle2, setEntreCalle2] = useState('');

    const [center, setCenter] = useState({ lat: 19.430154, lng: -99.137414 });
    const [zoom, setZoom] = useState(17);
    const [marker, setMarker] = useState(null);

    const alcaldiasCDMX = [
        'Álvaro Obregón',
        'Azcapotzalco',
        'Benito Juárez',
        'Coyoacán',
        'Cuajimalpa de Morelos',
        'Cuauhtémoc',
        'Gustavo A. Madero',
        'Iztacalco',
        'Iztapalapa',
        'Magdalena Contreras',
        'Miguel Hidalgo',
        'Milpa Alta',
        'Tláhuac',
        'Tlalpan',
        'Venustiano Carranza',
        'Xochimilco'
    ];

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };

    const showSuccessAlert = (message) => {
        Swal.fire({
            icon: 'success',
            title: 'Éxito',
            text: message,
            confirmButtonText: 'OK'
        });
    };

    const getDireccionById = async () => {
        try {
            const res = await axios.get(`${URI}?idDireccion=${ID_Direccion}`);
            const data = res.data;

            setCalle(data.calle || '');
            setNumero(data.numero || '');
            setAlcaldia(data.alcaldia || 'Álvaro Obregón');
            setCodigoPostal(data.codigo_postal || '');
            setReferencias(data.referencias || '');
            setEntreCalle1(data.entreCalle1 || '');
            setEntreCalle2(data.entreCalle2 || '');

            const [lat, lng] = data.coordenadas.split(',').map(parseFloat);
            const coords = { lat, lng };

            setCoordenadas(data.coordenadas || '');
            setMarker(coords);
            setCenter(coords);
        } catch (error) {
            console.error("Error al obtener la dirección:", error);
            showErrorAlert("No se pudo cargar la dirección.");
        }
    };

    const updateDireccion = async (e) => {
        e.preventDefault();

        if (!calle || !numero || !codigoPostal || !coordenadas || !referencias || !entreCalle1 || !entreCalle2) {
            showErrorAlert("Todos los campos son obligatorios.");
            return;
        }

        if (isNaN(codigoPostal)) {
            showErrorAlert("El código postal debe ser numérico.");
            return;
        }

        try {
            const payload = {
                idDireccion: parseInt(ID_Direccion),
                calle,
                numero,
                alcaldia,
                codigo_postal: codigoPostal,
                coordenadas,
                referencias,
                entreCalle1,
                entreCalle2
            };

            await axios.put(updURI, payload, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            showSuccessAlert("Dirección actualizada con éxito.");
            navigate('/showDeportivos/admin/null');
        } catch (error) {
            console.error("Error al actualizar la dirección:", error);
            showErrorAlert("Error al actualizar la dirección.");
        }
    };

    useEffect(() => {
        getDireccionById();
    }, []);

    return (
        <div className="formulario">
            <h1>Editar Dirección</h1>
            <form onSubmit={updateDireccion}>
                <div className="form-group">
                    <label>Calle</label>
                    <input type="text" value={calle} onChange={(e) => setCalle(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Número</label>
                    <input type="text" value={numero} onChange={(e) => setNumero(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Alcaldía</label>
                    <select value={alcaldia} onChange={(e) => setAlcaldia(e.target.value)} className="form-control">
                        {alcaldiasCDMX.map((alcaldia, index) => (
                            <option key={index} value={alcaldia}>{alcaldia}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Código Postal</label>
                    <input type="text" value={codigoPostal} onChange={(e) => setCodigoPostal(e.target.value)} className="form-control" />
                </div>
                <div>
                    <label>Coordenadas</label>
                    <input type="text" value={coordenadas} onChange={(e) => setCoordenadas(e.target.value)} className="form-control" disabled />
                </div>
                <div className="form-group">
                    <label>Referencias</label>
                    <input type="text" value={referencias} onChange={(e) => setReferencias(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Entre Calle 1</label>
                    <input type="text" value={entreCalle1} onChange={(e) => setEntreCalle1(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Entre Calle 2</label>
                    <input type="text" value={entreCalle2} onChange={(e) => setEntreCalle2(e.target.value)} className="form-control" />
                </div>
                <button type="submit" className="btn btn-primary">Actualizar Dirección</button>
            </form>
        </div>
    );
};

export default CompEditDireccion;
