/* global google */
import axios from "axios";
import React, { useState, useEffect, useRef } from "react";
import { Wrapper } from "@googlemaps/react-wrapper";
import { useNavigate } from "react-router-dom"
import Swal from "sweetalert2";
import { Link } from "react-router-dom";

const API_KEY = "AIzaSyCiSwQ835LzONcwinoIhq1zE-7keqrGKSs"; 

const URI = 'http://localhost:8000/createDireccion/'
const URICDE = 'http://localhost:8000/checkDireccionExistente/'


/* Inicio Funcionamiento de Maps */
// Componente para manejar el mapa
const Map = ({ center, zoom, onMapClick, marker }) => {
    const mapRef = useRef(null);
    const [map, setMap] = useState(null);
  
    useEffect(() => {
      if (!map && mapRef.current) {
        // Crear un nuevo mapa
        const newMap = new google.maps.Map(mapRef.current, {
          center,
          zoom,
        });
  
        // Agregar el manejador de clic en el mapa
        newMap.addListener("click", (event) => {
          if (onMapClick) {
            onMapClick(event.latLng);
          }
        });
  
        setMap(newMap);
      }
    }, [mapRef, map, center, zoom, onMapClick]);
  
    useEffect(() => {
      if (map && marker) {
        // Eliminar marcadores existentes
        map.markers && map.markers.forEach((m) => m.setMap(null));
        
        // Crear nuevo marcador
        const newMarker = new google.maps.Marker({
          position: marker,
          map,
        });
        
        map.markers = [newMarker];
      }
    }, [map, marker]);
  
    return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
  };
/* Fin Funcionamiento de Maps */

const CompCreateDireccion = () => {
    const navigate = useNavigate()
    const [Calle, setCalle] = useState('');
    const [Numero, setNumero] = useState('');
    const [Alcaldia, setAlcaldia] = useState('Álvaro Obregón');
    const [Codigo_Postal, setCodigoPostal] = useState('');
    const [Coordenadas, setCoordenadas] = useState('');
    const [Referencias, setReferencias] = useState('');
    const [Entre_Calle_1, setEntreCalle1] = useState('');
    const [Entre_Calle_2, setEntreCalle2] = useState('');
    //Para el mapa
    const [center, setCenter] = useState({ lat: 19.430154, lng: -99.137414 });
    const [zoom, setZoom] = useState(12);
    const [marker, setMarker] = useState(null);
    const searchInputRef = useRef(null);

    const handleMapClick = (latLng) => {
        const newMarker = { lat: latLng.lat(), lng: latLng.lng() };
        setMarker(newMarker);
        setCoordenadas(`${newMarker.lat}, ${newMarker.lng}`);
      };

    const alcaldiasCDMX = [
        'Álvaro Obregón',
        'Azcapotzalco',
        'Benito Juárez',
        'Coyoacán',
        'Cuajimalpa de Morelos',
        'Cuauhtémoc',
        'Gustavo A. Madero',
        'Iztacalco',
        'Iztapalapa',
        'Magdalena Contreras',
        'Miguel Hidalgo',
        'Milpa Alta',
        'Tláhuac',
        'Tlalpan',
        'Venustiano Carranza',
        'Xochimilco'
      ];

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };       

    useEffect(() => {
        // Configurar autocompletar en el campo de búsqueda
        if (searchInputRef.current) {
          const autocomplete = new google.maps.places.Autocomplete(searchInputRef.current);
    
          // Limitar los tipos de búsqueda a establecimientos (lugares)
          autocomplete.setTypes(["establishment"]);
    
          // Agregar filtro de palabras clave para solo buscar deportivos
          autocomplete.setFields(["name", "geometry", "types"]);
          
    
          // Define los límites de la Ciudad de México
          const mexicoCityBounds = new google.maps.LatLngBounds(
            new google.maps.LatLng(19.1500, -99.3250), // Suroeste de la Ciudad de México
            new google.maps.LatLng(19.6300, -98.9900) // Noreste de la Ciudad de México
          );
    
          // Establece los límites geográficos para restringir la búsqueda a la Ciudad de México
          autocomplete.setBounds(mexicoCityBounds);
    
          // Manejador cuando se selecciona un lugar
          autocomplete.addListener("place_changed", () => {
            const place = autocomplete.getPlace();
            
            if (place && place.geometry) {
              const location = place.geometry.location;
              const newCenter = {
                lat: location.lat(),
                lng: location.lng(),
              };
    
              // Centrar el mapa en la ubicación seleccionada y agregar un marcador
              setCenter(newCenter);
              setMarker(newCenter);
              setCoordenadas(`${newCenter.lat}, ${newCenter.lng}`);
            }
          });
        }
      }, [searchInputRef]);

    //Agregamos comprobación de direccion existente
    const checkDireccionExistente = async () => {
        try {
            const response = await axios.get(`${URICDE}?Calle=${Calle}&Numero=${Numero}&Alcaldia=${Alcaldia}&Codigo_Postal=${Codigo_Postal}`);
            return response.data.exists; // Suponiendo que el servidor devuelve un objeto con una propiedad 'exists'
        } catch (error) {
            console.error("Error al verificar la existencia de la dirección:", error);
            return false; // En caso de error, asumimos que la dirección no existe
        }
    };

    //Procedimiento para actualizar
    const store = async (e) => {
        e.preventDefault()

        //Comprobamos si la direccion existe
        const direccionExistente = await checkDireccionExistente();
        if (direccionExistente) {
            showErrorAlert("Ya existe un deportivo con esa dirección");
            return;
        }

        // Validar campos requeridos
        if (!Calle || !Numero || !Codigo_Postal || !Coordenadas || !Referencias || !Entre_Calle_1 || !Entre_Calle_2) {
            showErrorAlert("Todos los campos son requeridos");
            return;
        }

        // Validar que el código postal sea numérico y que tenga una longitud de 5
        if (isNaN(Codigo_Postal) || Codigo_Postal.length < 5) {
            showErrorAlert("El código postal debe ser numérico y debe contener cinco números");
            return;
        }


        // Validar coordenadas
        if (!isValidCoordinates(Coordenadas)) {
            showErrorAlert("Las coordenadas no tienen el formato correcto");
            return;
        }

        
        // Validar Numero
        if (!isValidNumero(Numero)) {
            showErrorAlert("El número debe ser un valor numérico o S/N");
            return;
        }
        //en este caso pasamos la URI mas la ID_Direccion, justo como se hizo en las pruebas con thunderclient
        try{
            const response = await axios.post(URI, {
                Calle: Calle, Numero: Numero, Alcaldia: Alcaldia,
                    Codigo_Postal: Codigo_Postal, Coordenadas: Coordenadas, Referencias: Referencias, Entre_Calle_1: Entre_Calle_1,
                    Entre_Calle_2: Entre_Calle_2                           
                })
                            // Verifica si la solicitud fue exitosa
                            if (response.status === 201) { //Hay que modificar el controlador para que retorne el valor de la ID Direccion
                                // Si es exitosa, obtén el ID de la dirección recién creada
                                const ID_Direccion = response.data.ID_Direccion;
                    
                                // Ahora tienes el ID_Direccion, puedes hacer lo que necesites con él
                                console.log("ID_Direccion:", ID_Direccion);
                                navigate(`/createDeportivo/${ID_Direccion}`);
                            }
            } catch (error) {
                console.error("Error al crear la dirección:", error);
            }
    }


    const handleAlcaldiaChange = (e) => {
        const selectedOption = e.target.options[e.target.selectedIndex];
        const selectedAlcaldia = selectedOption.innerText;
        setAlcaldia(selectedAlcaldia);
    };
    
    const isValidCoordinates = (Coordenadas) => {
        const regex = /^-?\d+\.\d+,\s*-?\d+\.\d+$/;
        return regex.test(Coordenadas);
    };
    
    const isValidNumero = (Numero) => {
        console.log(Numero)
        const regex = /^\d+$/;        
        if(Numero=="S/N" || Numero=="s/n"){
            return true
        }
        else{
            return regex.test(Numero);
        }
    };
    
    return (
        <div className="formulario">
            <h1>Ingresar nuevo deportivo:</h1>
            <h1>Direccion</h1>
            <form onSubmit={store}>
                <div className="form-group">
                    <label>Calle</label>
                    <input type="text" value={Calle} onChange={(e) => setCalle(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Número</label>
                    <input type="text" value={Numero} onChange={(e) => setNumero(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Alcaldía</label>
                    <select value={Alcaldia} onChange={handleAlcaldiaChange} className="form-control" required>
                        {alcaldiasCDMX.map((alcaldia, index) => (
                            <option key={index} value={alcaldia}>{alcaldia}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Código Postal</label>
                    <input type="text" value={Codigo_Postal} onChange={(e) => setCodigoPostal(e.target.value)} className="form-control" maxLength={5} required />
                </div>
                <div className="form-group">
                <div style={{ height: "100vh", width: "100%" }}>
                    <div style={{ padding: "10px" }}>                        
                        {/* Barra de búsqueda */}
                        <p><strong>Buscar Deportivo</strong></p>
                        <input
                        ref={searchInputRef}
                        type="text"
                        placeholder="Buscar deportivo en la Ciudad de México..."
                        className="form-control"
                        />

                        {/* Coordenadas del marcador actual */}
                        {marker && (
                        <div>
                            <label>Coordenadas (Latitud, Longitud):</label>
                            <input
                            type="text"
                            value={Coordenadas}
                            readOnly
                            className="form-control"
                            />
                            <Link
                            to={`https://www.google.com/maps/place/${Coordenadas}`}
                            className="btn btn-success"
                            target="_blank"
                            >
                            Ver en Google Maps
                            </Link>
                        </div>
                        )}
                    </div>

                    <Wrapper apiKey={API_KEY} render={(status) => <h1>{status}</h1>}>
                        <Map center={center} zoom={zoom} onMapClick={handleMapClick} marker={marker} />
                    </Wrapper>
                </div>
                    <label>Coordenadas (Latitud, Longitud)</label> 
                    <input type="text" value={Coordenadas} onChange={(e) => setCoordenadas(e.target.value)} className="form-control" required disablec/>
                </div>
                <div className="form-group">
                    <label>Referencias</label>
                    <input type="text" value={Referencias} onChange={(e) => setReferencias(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Entre Calle 1</label>
                    <input type="text" value={Entre_Calle_1} onChange={(e) => setEntreCalle1(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Entre Calle 2</label>
                    <input type="text" value={Entre_Calle_2} onChange={(e) => setEntreCalle2(e.target.value)} className="form-control" required />
                </div>
                <button type="submit" className="btn btn-primary">Ingresar dirección</button>
            </form>
        </div>
    );
}


export default CompCreateDireccion
