import React, { useState, useEffect } from 'react';
import axios from 'axios';

const URI = 'http://localhost:8000/';

const CompShowDeportivos = () => {
    const [deportivos, setDeportivo] = useState([]);
    useEffect(() => {
        getDeportivoInfo();
    }, []);

    const getDeportivoInfo = async () => {
        const res = await axios.get(`${URI}${'getDeportivoInfo'}`);
        setDeportivo(res.data);
    };

    const [expandedDeportivo, setExpandedDeportivo] = useState(null);
    const [expandedSubAccordion, setExpandedSubAccordion] = useState(null);

    const toggleAccordion = (idDeportivo) => {
        setExpandedDeportivo(idDeportivo === expandedDeportivo ? null : idDeportivo);
    };

    const toggleSubAccordion = (index) => {
        setExpandedSubAccordion(index === expandedSubAccordion ? null : index);
    };

    const handleGuardarClick = () => {
        // Aquí puedes enviar los datos editados al servidor usando Axios u otra librería
        // Recuerda enviar editedDireccion, editedEncargado y editedCancha al servidor
        // Luego de enviar los datos, puedes realizar las acciones necesarias, como cerrar el modal o mostrar un mensaje de éxito
    };

    return (
        <div className="deportivos-accordion">
            {deportivos.map((deportivo) => (
                <div key={deportivo.ID_Deportivo} className="deportivo-card">
                    <div className="accordion-header" onClick={() => toggleAccordion(deportivo.ID_Deportivo)}>
                        <h2>{deportivo.Nombre}</h2>
                        <span>{expandedDeportivo === deportivo.ID_Deportivo ? '-' : '+'}</span>
                    </div>
                    {expandedDeportivo === deportivo.ID_Deportivo && (
                        <div className="accordion-content">
                            {deportivo.direccion && (
                                <div className="sub-accordion">
                                    <h3 onClick={() => toggleSubAccordion(0)}>Dirección</h3>
                                    {expandedSubAccordion === 0 && (
                                        <div className="sub-accordion-content">
                                            <input type="text" name="Calle" defaultValue={deportivo.direccion.Calle} />
                                            <input type="text" name="Numero" defaultValue={deportivo.direccion.Numero} />
                                            {/* Agrega más campos según sea necesario para editar la dirección */}
                                        </div>
                                    )}
                                </div>
                            )}
                            {deportivo.encargado && (
                                <div className="sub-accordion">
                                    <h3 onClick={() => toggleSubAccordion(1)}>Encargado</h3>
                                    {expandedSubAccordion === 1 && (
                                        <div className="sub-accordion-content">
                                            <input type="text" name="Nombre" defaultValue={deportivo.encargado.Nombre} />
                                            <input type="text" name="Primer_Apellido" defaultValue={deportivo.encargado.Primer_Apellido} />
                                            {/* Agrega más campos según sea necesario para editar el encargado */}
                                        </div>
                                    )}
                                </div>
                            )}
                            {deportivo.cancha && (
                                <div className="sub-accordion">
                                    <h3 onClick={() => toggleSubAccordion(2)}>Canchas</h3>
                                    {expandedSubAccordion === 2 && (
                                        <div className="sub-accordion-content">
                                            {/* Agrega campos de entrada para editar la información de las canchas */}
                                            {deportivo.cancha.map((cancha) => (
                                                <div key={cancha.ID_Cancha} className="cancha-info">
                                                    <input type="text" name="Tipo_Cancha" defaultValue={cancha.Tipo_Cancha} />
                                                    <input type="text" name="Numero_Cancha" defaultValue={cancha.Numero_Cancha} />
                                                    {/* Agrega más campos según sea necesario para editar las canchas */}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    )}
                </div>
            ))}
            {/* Botón para guardar los cambios */}
            <button onClick={handleGuardarClick}>Guardar</button>
        </div>
    );
};

export default CompShowDeportivos;
