/* global google */
import React, { useEffect, useRef } from 'react';

<script
    src="https://maps.googleapis.com/maps/api/js?key=AIzaSyCiSwQ835LzONcwinoIhq1zE-7keqrGKSs"
    async
    onload={() => console.log("Google Maps API loaded")}
></script>

const GoogleMap = ({ coordinates }) => {
    const mapRef = useRef(null);

    useEffect(() => {
        if (mapRef.current && window.google) {
            // Crea un nuevo mapa
            const map = new google.maps.Map(mapRef.current, {
                center: { lat: coordinates.lat, lng: coordinates.lng },
                zoom: 17,
            });

            // Crea un marcador en las coordenadas proporcionadas
            new google.maps.Marker({
                position: { lat: coordinates.lat, lng: coordinates.lng },
                map: map,
            });
        }
    }, [coordinates]);

    return <div ref={mapRef} style={{ height: '300px', width: '100%' }}></div>;
};

export default GoogleMap;
