import axios from "axios";
import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const URI = 'http://localhost:8000/createHorarios/';

const CompCreateHorario = () => {
    const navigate = useNavigate();
    const {ID_Deportivo} = useParams() 
    const [horarios, setHorarios] = useState([
        { sEdiaSemanaSemana: 'Lunes', horarioApertura: '', horarioCierre: '', ID_Deportivo: ID_Deportivo },
        { sEdiaSemanaSemana: 'Martes', horarioApertura: '', horarioCierre: '', ID_Deportivo: ID_Deportivo },
        { sEdiaSemanaSemana: 'Miércoles', horarioApertura: '', horarioCierre: '', ID_Deportivo: ID_Deportivo },
        { diaSemana: 'Jueves', horarioApertura: '', horarioCierre: '', ID_Deportivo: ID_Deportivo },
        { diaSemana: 'Viernes', horarioApertura: '', horarioCierre: '', ID_Deportivo: ID_Deportivo },
        { diaSemana: 'Sábado', horarioApertura: '', horarioCierre: '', ID_Deportivo: ID_Deportivo },
        { diaSemana: 'Domingo', horarioApertura: '', horarioCierre: '', ID_Deportivo: ID_Deportivo }
    ]);
    
    const store = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(URI, { horarios });
    
            if (response.status === 201) {
                showErrorAlert("OK")
                //navigate(`/createEncargado/${ID_Deportivo}`);
            } else {
                showErrorAlert("Hubo un error al crear los horarios. Inténtalo de nuevo.");
            }
        
        } catch (error) {
            console.error("Error:", error);
            showErrorAlert("Hubo un error al crear los horarios. Inténtalo de nuevo.");
        }
    };
    

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };

    const handleHorarioChange = (index, field, value) => {
        const updatedHorarios = [...horarios];
        updatedHorarios[index][field] = value;
        setHorarios(updatedHorarios);
    };
    
    return (
            <form onSubmit={store}>
                <div className="form-group">
                    {horarios.map((horario, index) => (
                        <div key={index}>
                            <label>{horario.diaSemana}</label>
                            <div>
                                <input
                                    type="time"
                                    value={horario.horarioApertura}
                                    onChange={(e) => handleHorarioChange(index, 'horarioApertura', e.target.value)}
                                    className="form-control"
                                    placeholder="Hora de apertura"
                                />
                                <span> a </span>
                                <input
                                    type="time"
                                    value={horario.horarioCierre}
                                    onChange={(e) => handleHorarioChange(index, 'horarioCierre', e.target.value)}
                                    className="form-control"
                                    placeholder="Hora de cierre"
                                />
                            </div>
                        </div>
                    ))}
                </div>
        <button type="submit" className="btn btn-primary">Crear Horarios</button>
    </form>
    )
}

export default CompCreateHorario;
