import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const URI = 'http://localhost:8000/';

const CompShowDeportivos = () => {
    const [deportivos, setDeportivos] = useState([]);
    useEffect(() => {
        getDeportivoInfo();
    }, []);

    const getDeportivoInfo = async () => {
        const res = await axios.get(`${URI}getDeportivoInfo`);
        setDeportivos(res.data);
    }

    const deleteDeportivo = async (id) => {
        await axios.delete(`${URI}deleteDeportivo${id}`);
        getDeportivoInfo();
    }

    const [expandedDeportivo, setExpandedDeportivo] = useState(null);
    const [expandedSubAccordion, setExpandedSubAccordion] = useState(null);

    const toggleAccordion = (idDeportivo) => {
        setExpandedDeportivo(idDeportivo === expandedDeportivo ? null : idDeportivo);
    };

    const toggleSubAccordion = (index) => {
        setExpandedSubAccordion(index === expandedSubAccordion ? null : index);
    };

    const handleEditAddress = (id) => {
        // Implementa la lógica para editar la dirección
        console.log('Editar dirección del deportivo con ID:', id);
    };

    const handleEditManager = (id) => {
        // Implementa la lógica para editar el encargado
        console.log('Editar encargado del deportivo con ID:', id);
    };

    const handleEditField = (fieldName, value, id) => {
        // Implementa la lógica para editar un campo específico
        console.log(`Editar ${fieldName} del deportivo con ID ${id}:`, value);
    };

    return (
        <div className="deportivos-accordion">
            {deportivos.map((deportivo) => (
                <div key={deportivo.ID_Deportivo} className="deportivo-card">
                    <div className="accordion-header" onClick={() => toggleAccordion(deportivo.ID_Deportivo)}>
                        <h2>{deportivo.Nombre}</h2>
                        <span>{expandedDeportivo === deportivo.ID_Deportivo ? '-' : '+'}</span>
                    </div>
                    {expandedDeportivo === deportivo.ID_Deportivo && (
                        <div className="accordion-content">
                            {deportivo.direccion && (
                                <div className="sub-accordion">
                                    <h3 onClick={() => toggleSubAccordion(0)}>Dirección</h3>
                                    {expandedSubAccordion === 0 && (
                                        <div className="sub-accordion-content">
                                            <p><strong>Calle:</strong> {deportivo.direccion.Calle}</p>
                                            <p><strong>Número:</strong> {deportivo.direccion.Numero}</p>
                                            <p><strong>Alcaldía:</strong> {deportivo.direccion.Alcaldia}</p>
                                            <p><strong>Código Postal:</strong> {deportivo.direccion.Codigo_Postal}</p>
                                            <p><strong>Referencias:</strong> {deportivo.direccion.Referencias}</p>
                                            <p><strong>Entre Calle 1:</strong> {deportivo.direccion.Entre_Calle_1}</p>
                                            <p><strong>Entre Calle 2:</strong> {deportivo.direccion.Entre_Calle_2}</p>
                                            <button onClick={() => handleEditField('Calle', deportivo.direccion.Calle, deportivo.ID_Deportivo)}>Editar Calle</button>
                                            {/* Agrega más botones de edición según los campos */}
                                        </div>
                                    )}
                                </div>
                            )}
                            {deportivo.encargado && (
                                <div className="sub-accordion">
                                    <h3 onClick={() => toggleSubAccordion(1)}>Encargado</h3>
                                    {expandedSubAccordion === 1 && (
                                        <div className="sub-accordion-content">
                                            <p><strong>Nombre:</strong> {deportivo.encargado.Nombre}</p>
                                            <p><strong>Primer Apellido:</strong> {deportivo.encargado.Primer_Apellido}</p>
                                            <p><strong>Segundo Apellido:</strong> {deportivo.encargado.Segundo_Apellido}</p>
                                            <p><strong>Teléfono Fijo:</strong> {deportivo.encargado.Telefono_Fijo}</p>
                                            <p><strong>Teléfono Móvil:</strong> {deportivo.encargado.Telefono_Movil}</p>
                                            <p><strong>Cargo:</strong> {deportivo.encargado.Cargo}</p>
                                            <p><strong>RFC/CURP:</strong> {deportivo.encargado.RFC_CURP}</p>
                                            <button onClick={() => handleEditField('Nombre', deportivo.encargado.Nombre, deportivo.ID_Deportivo)}>Editar Nombre</button>
                                            {/* Agrega más botones de edición según los campos */}
</div>
)}
</div>
)}
{deportivo.cancha && (
<div className="sub-accordion">
<h3 onClick={() => toggleSubAccordion(2)}>Canchas</h3>
{expandedSubAccordion === 2 && (
<div className="sub-accordion-content">
{deportivo.cancha.map((cancha) => (
<div key={cancha.ID_Cancha} className="cancha-info">
<p><strong>Tipo:</strong> {cancha.Tipo_Cancha}</p>
<p><strong>Número:</strong> {cancha.Numero_Cancha}</p>
<p><strong>Material del Piso:</strong> {cancha.Material_Piso}</p>
</div>
))}
</div>
)}
</div>
)}
</div>
)}
</div>
))}
</div>
);
}

export default CompShowDeportivos;
