import React, { useState } from 'react';

const MyComponent = () => {
    // Define un estado local para controlar la visibilidad del <div>
    const [isVisible, setIsVisible] = useState(false);

    // Define una función para togglear la visibilidad
    const toggleVisibility = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div>
            {/* Botón para togglear la visibilidad */}
            <button onClick={toggleVisibility}>
                {isVisible ? 'Esconder' : 'Mostrar'} div
            </button>

            {/* Solo renderiza el <div> si isVisible es true */}
            {isVisible && (
                <div>
                    <p>Este es el div que se puede esconder o mostrar.</p>
                </div>
            )}
        </div>
    );
};

export default MyComponent;
