import axios from "axios";
import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { Link } from "react-router-dom";
import Swal from "sweetalert2";


const URI = 'http://localhost:8000/oneDireccion/'
const updURI = 'http://localhost:8000/updateDireccion/'


const CompEditDireccion = () => {
    const navigate = useNavigate()
    const {ID_Direccion} = useParams() //se guarda como un objeto, por ello los corchetes

    const [Calle, setCalle] = useState('');
    const [Numero, setNumero] = useState('');
    const [Alcaldia, setAlcaldia] = useState('Álvaro Obregón');
    const [Codigo_Postal, setCodigoPostal] = useState('');
    const [Coordenadas, setCoordenadas] = useState('');
    const [Referencias, setReferencias] = useState('');
    const [Entre_Calle_1, setEntreCalle1] = useState('');
    const [Entre_Calle_2, setEntreCalle2] = useState('');
    const alcaldiasCDMX = [
        'Álvaro Obregón',
        'Azcapotzalco',
        'Benito Juárez',
        'Coyoacán',
        'Cuajimalpa de Morelos',
        'Cuauhtémoc',
        'Gustavo A. Madero',
        'Iztacalco',
        'Iztapalapa',
        'Magdalena Contreras',
        'Miguel Hidalgo',
        'Milpa Alta',
        'Tláhuac',
        'Tlalpan',
        'Venustiano Carranza',
        'Xochimilco'
      ];


      const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };      
    // Procedimiento para actualizar
    const update = async (e) => {
        e.preventDefault();

        // Validar campos requeridos
        if (!Calle || !Numero || !Codigo_Postal || !Coordenadas || !Referencias || !Entre_Calle_1 || !Entre_Calle_2) {
            showErrorAlert("Todos los campos son requeridos");
            return;
        }

        // Validar que el código postal sea numérico
        if (isNaN(Codigo_Postal)) {
            showErrorAlert("El código postal debe ser numérico");
            return;
        }

        // Enviar la solicitud de actualización
        await axios.put(updURI + ID_Direccion, {
            Calle: Calle, 
            Numero: Numero, 
            Alcaldia: Alcaldia,
            Codigo_Postal: Codigo_Postal, 
            Coordenadas: Coordenadas, 
            Referencias: Referencias, 
            Entre_Calle_1: Entre_Calle_1,
            Entre_Calle_2: Entre_Calle_2       
        });

        // Regresar al principio
        navigate('/');
    }

    const getDireccionById = async () => {
        const res = await axios.get(URI+ID_Direccion);
        try {
            setCalle(res.data.Calle || '');
            setNumero(res.data.Numero || '');
            setAlcaldia(res.data.Alcaldia || 'Álvaro Obregón');
            setCodigoPostal(res.data.Codigo_Postal || '');
            setCoordenadas(res.data.Coordenadas || '');
            setReferencias(res.data.Referencias || '');
            setEntreCalle1(res.data.Entre_Calle_1 || '');
            setEntreCalle2(res.data.Entre_Calle_2 || '');
        } catch (error) {
            console.log("Error al obtener datos", error);
        }
    }

    useEffect(() => { 
        getDireccionById();
    },[]);

    const handleAlcaldiaChange = (e) => {
        const selectedOption = e.target.options[e.target.selectedIndex];
        const selectedAlcaldia = selectedOption.innerText;
        setAlcaldia(selectedAlcaldia);
    };

    return (
        <div className="formulario">
            <h1>Editar dirección existente</h1>
            <form onSubmit={update}>
                <div className="form-group">
                    <label>Calle</label>
                    <input type="text" value={Calle} onChange={(e) => setCalle(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Número</label>
                    <input type="text" value={Numero} onChange={(e) => setNumero(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Alcaldía</label>
                    <select value={Alcaldia} onChange={handleAlcaldiaChange}>
                        {alcaldiasCDMX.map((alcaldia, index) => (
                            <option key={index} value={alcaldia}>{alcaldia}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Código Postal</label>
                    <input type="text" value={Codigo_Postal} onChange={(e) => setCodigoPostal(e.target.value)} className="form-control" maxLength={5}/>
                </div>
                <div className="form-group">
                    <label>Coordenadas (ej. 19.68254, -99.07182)</label>
                    <input type="text" value={Coordenadas} onChange={(e) => setCoordenadas(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Referencias</label>
                    <input type="text" value={Referencias} onChange={(e) => setReferencias(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Entre Calle 1</label>
                    <input type="text" value={Entre_Calle_1} onChange={(e) => setEntreCalle1(e.target.value)} className="form-control" />
                </div>
                <div className="form-group">
                    <label>Entre Calle 2</label>
                    <input type="text" value={Entre_Calle_2} onChange={(e) => setEntreCalle2(e.target.value)} className="form-control" />
                </div>
                <button type="submit" className="btn btn-primary">Actualizar dirección</button>
            </form>
        </div>
    )
}


export default CompEditDireccion
