import axios from "axios";
import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const URI = 'http://localhost:8000/createHorarios/';

const CompCreateHorario = () => {
    const navigate = useNavigate();
    const { ID_Deportivo } = useParams();
    const [horarios, setHorarios] = useState([
        { dia_semana: 'Lunes', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { dia_semana: 'Martes', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { dia_semana: 'Miércoles', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { dia_semana: 'Jueves', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { dia_semana: 'Viernes', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { dia_semana: 'Sábado', hora_apertura: '', hora_cierre: '', inhabil: '' },
        { dia_semana: 'Domingo', hora_apertura: '', hora_cierre: '', inhabil: '' }
    ]);

    const store = async (e) => {
        e.preventDefault();
        try {
                    const formattedHorarios = horarios.map((horario) => ({
                        ID_Horario: null, // Se deja este campo como null si es para una nueva inserción
                        ID_Deportivo: ID_Deportivo,
                        dia_semana: horario.dia_semana,
                        hora_apertura: horario.hora_apertura,
                        hora_cierre: horario.hora_cierre,
                        inhabil: horario.inhabil
                    }));
                    if(!isValidHoras(hora_apertura, hora_cierre)){
                        const postData = { horarios: formattedHorarios }; // Objeto con la clave "horarios"
                        await axios.post(URI, postData);
                        navigate(`/createEncargado/${ID_Deportivo}`);
            }

        } catch (error) {
            console.error("Error:", error);
            showErrorAlert("Hubo un error al crear los horarios. Inténtalo de nuevo.");
        }
    };

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };

    const handleHorarioChange = (index, field, value) => {
        const updatedHorarios = [...horarios];
        updatedHorarios[index][field] = value;
        setHorarios(updatedHorarios);
    };

    const handleInhabilChange = (index) => {
        const updatedHorarios = [...horarios];
        updatedHorarios[index].inhabil = !updatedHorarios[index].inhabil;
        setHorarios(updatedHorarios);
    };

    const isValidHoras = (hora_apertura, hora_cierre) => {
        if (hora_apertura === hora_cierre)
            return false
        else
            return true
    }

    return (
        <form onSubmit={store}>
            <div className="formulario">
                <h1>Ingresar Horarios del Deportivo</h1>
                {horarios.map((horario, index) => (
                    <div key={index}>
                        <label><h3>{horario.dia_semana}</h3></label>
                        <div>
                            <input
                                type="time"
                                value={horario.hora_apertura}
                                onChange={(e) => handleHorarioChange(index, 'hora_apertura', e.target.value)}
                                className="form-control"
                                placeholder="Hora de apertura"
                                disabled={horario.inhabil} // Deshabilita el input si es inhabil
                            />
                            <span> a </span>
                            <input
                                type="time"
                                value={horario.hora_cierre}
                                onChange={(e) => handleHorarioChange(index, 'hora_cierre', e.target.value)}
                                className="form-control"
                                placeholder="Hora de cierre"
                                disabled={horario.inhabil} // Deshabilita el input si es inhabil
                            />
                            <label>
                                <input
                                    type="checkbox"
                                    checked={horario.inhabil}
                                    onChange={() => handleInhabilChange(index)}
                                />
                                Inhabil
                            </label>
                        </div>
                    </div>
                ))}
                <button type="submit" className="btn btn-primary">Crear Horarios</button>
            </div>
        </form>
    )
}

export default CompCreateHorario;
