/* global google */
import axios from "axios";
import React, { useState, useEffect, useRef } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const URI = 'http://localhost:8000/createCancha';
const URICNCE = 'http://localhost:8000/getCanchaDeportivo';

/* Inicio Funcionamiento de Maps */
const Map = ({ center, zoom, onMapClick, marker }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (!map && mapRef.current && window.google) {
      const newMap = new google.maps.Map(mapRef.current, {
        center,
        zoom,
      });

      newMap.addListener("click", (event) => {
        if (onMapClick) {
          onMapClick(event.latLng);
        }
      });

      setMap(newMap);
    } else if (map) {
      map.setCenter(center);
      map.setZoom(zoom);
    }
  }, [mapRef, map, center, zoom, onMapClick]);

  useEffect(() => {
    if (map && marker) {
      if (map.markers) map.markers.forEach((m) => m.setMap(null));
      const newMarker = new google.maps.Marker({
        position: marker,
        map,
      });
      map.markers = [newMarker];
    }
  }, [map, marker]);

  return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
};
/* Fin Funcionamiento de Maps */

const CompCreateCancha = () => {
  const navigate = useNavigate();
  const { ID_Deportivo, coordsParam, RFC_CURP } = useParams();
  const [Tipo_Cancha, setTipoCancha] = useState('Fútbol');
  const [Numero_Cancha, setNumeroCancha] = useState('');
  const [Material_Piso, setMaterialPiso] = useState('Césped');
  const [Techado, setTechado] = useState(false);
  const [Con_Gradas, setConGradas] = useState(false);
  const [Estado, setEstado] = useState('Buena Condición');
  const [Capacidad_Jugadores, setCapacidadJugadores] = useState('');
  const [Coordenadas, setCoordenadas] = useState('');
  const [formErrors, setFormErrors] = useState({});
  const [center, setCenter] = useState({ lat: parseFloat(coordsParam.split(',')[0]), lng: parseFloat(coordsParam.split(',')[1]) });
  const [zoom, setZoom] = useState(17);
  const [marker, setMarker] = useState(null);

  const handleMapClick = (latLng) => {
    const newMarker = { lat: latLng.lat(), lng: latLng.lng() };
    setMarker(newMarker);
    setCoordenadas(`${newMarker.lat}, ${newMarker.lng}`);
    setCenter(newMarker);
  };

  const store = async (e) => {
    e.preventDefault();

    if (!Coordenadas || !Numero_Cancha || !Capacidad_Jugadores) {
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'Todos los campos son obligatorios.',
      });
      return;
    }

    try {
      await axios.post(URI, {
        Tipo_Cancha,
        Numero_Cancha,
        Material_Piso,
        Techado,
        Con_Gradas,
        Estado,
        Capacidad_Jugadores,
        ID_Deportivo,
        Coordenadas
      });

      Swal.fire("Éxito", "La cancha fue creada correctamente.", "success");
      navigate(`/showDeportivos/enc/${RFC_CURP}`);
    } catch (error) {
      console.error("Error al crear la cancha:", error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'Ocurrió un error al guardar la cancha.',
      });
    }
  };

  return (
    <div className="formulario">
      <h1>Ingresar nueva cancha</h1>
      <form onSubmit={store}>
        <div className="form-group">
          <label>Coordenadas</label>
          <input type="text" value={Coordenadas} disabled className="form-control" />
        </div>
        <div style={{ height: "400px", width: "100%" }}>
          <Map center={center} zoom={zoom} onMapClick={handleMapClick} marker={marker} />
        </div>
        <div className="form-group">
          <label>Tipo Cancha</label>
          <select value={Tipo_Cancha} onChange={(e) => setTipoCancha(e.target.value)} className="form-control">
            <option value="Fútbol">Fútbol</option>
            <option value="Básquetbol">Básquetbol</option>
            <option value="Tenis">Tenis</option>
          </select>
        </div>
        <div className="form-group">
          <label>Numero de Cancha</label>
          <input type="text" value={Numero_Cancha} onChange={(e) => setNumeroCancha(e.target.value)} className="form-control" />
        </div>
        <div className="form-group">
          <label>Capacidad de Jugadores</label>
          <input type="text" value={Capacidad_Jugadores} onChange={(e) => setCapacidadJugadores(e.target.value)} className="form-control" />
        </div>
        <button type="submit" className="btn btn-primary">Crear Cancha</button>
      </form>
    </div>
  );
};

export default CompCreateCancha;
