import axios from "axios";
import { useState } from "react"
import { useNavigate } from "react-router-dom"
import Swal from "sweetalert2";
import { Link } from "react-router-dom";

const URI = 'http://localhost:8000/createDireccion/'
const URICDE = 'http://localhost:8000/checkDireccionExistente/'
const CompCreateDireccion = () => {
    const navigate = useNavigate()
    const [Calle, setCalle] = useState('');
    const [Numero, setNumero] = useState('');
    const [Alcaldia, setAlcaldia] = useState('Álvaro Obregón');
    const [Codigo_Postal, setCodigoPostal] = useState('');
    const [Coordenadas, setCoordenadas] = useState('');
    const [Referencias, setReferencias] = useState('');
    const [Entre_Calle_1, setEntreCalle1] = useState('');
    const [Entre_Calle_2, setEntreCalle2] = useState('');
    const alcaldiasCDMX = [
        'Álvaro Obregón',
        'Azcapotzalco',
        'Benito Juárez',
        'Coyoacán',
        'Cuajimalpa de Morelos',
        'Cuauhtémoc',
        'Gustavo A. Madero',
        'Iztacalco',
        'Iztapalapa',
        'Magdalena Contreras',
        'Miguel Hidalgo',
        'Milpa Alta',
        'Tláhuac',
        'Tlalpan',
        'Venustiano Carranza',
        'Xochimilco'
      ];

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };       

    //Agregamos comprobación de direccion existente
    const checkDireccionExistente = async () => {
        try {
            const response = await axios.get(`${URIget}?Calle=${Calle}&Numero=${Numero}&Alcaldia=${Alcaldia}&Codigo_Postal=${Codigo_Postal}`);
            return response.data.exists; // Suponiendo que el servidor devuelve un objeto con una propiedad 'exists'
        } catch (error) {
            console.error("Error al verificar la existencia de la dirección:", error);
            return false; // En caso de error, asumimos que la dirección no existe
        }
    };

    //Procedimiento para actualizar
    const store = async (e) => {
        e.preventDefault()

        const direccionExistente = await checkDireccionExistente();
        if (direccionExistente) {
            showErrorAlert("Ya existe una dirección con los mismos datos");
            return;
        }

        // Validar campos requeridos
        if (!Calle || !Numero || !Codigo_Postal || !Coordenadas || !Referencias || !Entre_Calle_1 || !Entre_Calle_2) {
            showErrorAlert("Todos los campos son requeridos");
            return;
        }

        // Validar que el código postal sea numérico y que tenga una longitud de 5
        if (isNaN(Codigo_Postal) || Codigo_Postal.length < 5) {
            showErrorAlert("El código postal debe ser numérico y debe contener cinco números");
            return;
        }


        // Validar coordenadas
        if (!isValidCoordinates(Coordenadas)) {
            showErrorAlert("Las coordenadas no tienen el formato correcto");
            return;
        }

        // Validar Numero
        if (!isValidNumero(Numero)) {
            showErrorAlert("El numero no tiene el formato correcto");
            return;
        }
        //en este caso pasamos la URI mas la ID_Direccion, justo como se hizo en las pruebas con thunderclient
        try{
            const response = await axios.post(URI, {
                Calle: Calle, Numero: Numero, Alcaldia: Alcaldia,
                    Codigo_Postal: Codigo_Postal, Coordenadas: Coordenadas, Referencias: Referencias, Entre_Calle_1: Entre_Calle_1,
                    Entre_Calle_2: Entre_Calle_2                           
                })
                            // Verifica si la solicitud fue exitosa
                            if (response.status === 201) { //Hay que modificar el controlador para que retorne el valor de la ID Direccion
                                // Si es exitosa, obtén el ID de la dirección recién creada
                                const ID_Direccion = response.data.ID_Direccion;
                    
                                // Ahora tienes el ID_Direccion, puedes hacer lo que necesites con él
                                console.log("ID_Direccion:", ID_Direccion);
                                navigate(`/createDeportivo/${ID_Direccion}`);
                            }
            } catch (error) {
                console.error("Error al crear la dirección:", error);
            }
    }


    const handleAlcaldiaChange = (e) => {
        const selectedOption = e.target.options[e.target.selectedIndex];
        const selectedAlcaldia = selectedOption.innerText;
        setAlcaldia(selectedAlcaldia);
    };
    
    const isValidCoordinates = (coordinates) => {
        const regex = /^-?\d+\.\d+,\s*-?\d+\.\d+$/;
        return regex.test(coordinates);
    };
    
    const isValidNumero = (Numero) => {
        const regex = /^\d+$/;
        return regex.test(Numero);
    };
    
    return (
        <div className="formulario">
            <h1>Ingresar nuevo deportivo:</h1>
            <h1>Direccion</h1>
            <form onSubmit={store}>
                <div className="form-group">
                    <label>Calle</label>
                    <input type="text" value={Calle} onChange={(e) => setCalle(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Número</label>
                    <input type="text" value={Numero} onChange={(e) => setNumero(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Alcaldía</label>
                    <select value={Alcaldia} onChange={handleAlcaldiaChange} className="form-control" required>
                        {alcaldiasCDMX.map((alcaldia, index) => (
                            <option key={index} value={alcaldia}>{alcaldia}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Código Postal</label>
                    <input type="text" value={Codigo_Postal} onChange={(e) => setCodigoPostal(e.target.value)} className="form-control" maxLength={5} required />
                </div>
                <div className="form-group">
                    <label>Coordenadas (Latitud, Longitud)</label> 
                    <input type="text" value={Coordenadas} onChange={(e) => setCoordenadas(e.target.value)} className="form-control" required /><Link to={`https://maps.google.com`} className='btn btn-success' target="_blank"><i class="fa-solid fa-map-pin"></i></Link>
                </div>
                <div className="form-group">
                    <label>Referencias</label>
                    <input type="text" value={Referencias} onChange={(e) => setReferencias(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Entre Calle 1</label>
                    <input type="text" value={Entre_Calle_1} onChange={(e) => setEntreCalle1(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Entre Calle 2</label>
                    <input type="text" value={Entre_Calle_2} onChange={(e) => setEntreCalle2(e.target.value)} className="form-control" required />
                </div>
                <button type="submit" className="btn btn-primary">Ingresar dirección</button>
            </form>
        </div>
    );
}


export default CompCreateDireccion
