import axios from "axios";
import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { Link } from "react-router-dom";
import Swal from "sweetalert2";

const URI = 'http://localhost:8000/oneDireccion/'; //uri para obtener informacion de la direccion a editar
const updURI = 'http://localhost:8000/updateDireccion/'; //uri para actualizar la informacion de la direccion
const URICDE = 'http://localhost:8000/allDirecciones/' //uri para comprobar si la direccion ya existe


const CompEditDireccion = () => {
    const navigate = useNavigate();
    const { ID_Direccion } = useParams(); 

    const [Calle, setCalle] = useState('');
    const [Numero, setNumero] = useState('');
    const [Alcaldia, setAlcaldia] = useState('Álvaro Obregón');
    const [Codigo_Postal, setCodigoPostal] = useState('');
    const [Coordenadas, setCoordenadas] = useState('');
    const [Referencias, setReferencias] = useState('');
    const [Entre_Calle_1, setEntreCalle1] = useState('');
    const [Entre_Calle_2, setEntreCalle2] = useState('');

    const alcaldiasCDMX = [
        'Álvaro Obregón',
        'Azcapotzalco',
        'Benito Juárez',
        'Coyoacán',
        'Cuajimalpa de Morelos',
        'Cuauhtémoc',
        'Gustavo A. Madero',
        'Iztacalco',
        'Iztapalapa',
        'Magdalena Contreras',
        'Miguel Hidalgo',
        'Milpa Alta',
        'Tláhuac',
        'Tlalpan',
        'Venustiano Carranza',
        'Xochimilco'
    ];

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };      

    //Agregamos comprobación de direccion existente
    const checkDireccionExistente = async () => {
        try {
            const response = await axios.get(URICDE);
            const direcciones = response.data;
    
            // Verifica si alguna dirección existente coincide con los datos de la dirección que estás editando
            const direccionRepetida = direcciones.find(direccion => (
                direccion.Calle === Calle &&
                direccion.Numero === Numero &&
                direccion.Alcaldia === Alcaldia &&
                direccion.Codigo_Postal === Codigo_Postal &&
                direccion.ID_Direccion !== ID_Direccion
            ));
            //Parseamos el id de la direccion a entero para poder hacer la comparación (muy importante)
            console.log("ID de la direccion repetida, ID direccion actual",direccionRepetida.ID_Direccion, parseInt(ID_Direccion))
            if(direccionRepetida.ID_Direccion === ID_Direccion){
                return false
            }
            
            else{// Si encuentra una dirección repetida, devuelve true
                // de lo contrario, devuelve false
                return direccionRepetida !== undefined;
            }
        } catch (error) {
            console.error("Error al verificar la existencia de la dirección:", error);
            return false; // En caso de error, asumimos que la dirección no existe
        }
    };

    const update = async (e) => {
        e.preventDefault();
        
        //Comprobamos si la direccion existe
        const direccionExistente = await checkDireccionExistente();
        
        if (direccionExistente) {
            showErrorAlert("Ya existe un deportivo con esa dirección");
            return;
        }
        

        // Validar campos requeridos
        if (!Calle || !Numero || !Codigo_Postal || !Coordenadas || !Referencias || !Entre_Calle_1 || !Entre_Calle_2) {
            showErrorAlert("Todos los campos son requeridos");
            return;
        }

        // Validar que el código postal sea numérico
        if (isNaN(Codigo_Postal)) {
            showErrorAlert("El código postal debe ser numérico");
            return;
        }

        // Validar coordenadas
        if (!isValidCoordinates(Coordenadas)) {
            showErrorAlert("Las coordenadas no tienen el formato correcto");
            return;
        }

        // Validar Numero
        if (!isValidNumero(Numero)) {
            showErrorAlert("Este campo debe ser un valor numérico o S/N");
            return;
        }

        // Enviar la solicitud de actualización
        await axios.put(updURI + ID_Direccion, {
            Calle: Calle, 
            Numero: Numero, 
            Alcaldia: Alcaldia,
            Codigo_Postal: Codigo_Postal, 
            Coordenadas: Coordenadas, 
            Referencias: Referencias, 
            Entre_Calle_1: Entre_Calle_1,
            Entre_Calle_2: Entre_Calle_2       
        });

        // Regresar al principio
        navigate('/');
    }

    const getDireccionById = async () => {
        const res = await axios.get(URI + ID_Direccion);
        try {
            setCalle(res.data.Calle || '');
            setNumero(res.data.Numero || '');
            setAlcaldia(res.data.Alcaldia || 'Álvaro Obregón');
            setCodigoPostal(res.data.Codigo_Postal || '');
            setCoordenadas(res.data.Coordenadas || '');
            setReferencias(res.data.Referencias || '');
            setEntreCalle1(res.data.Entre_Calle_1 || '');
            setEntreCalle2(res.data.Entre_Calle_2 || '');
        } catch (error) {
            console.log("Error al obtener datos", error);
        }
    }

    useEffect(() => { 
        getDireccionById();
    }, []);

    const handleAlcaldiaChange = (e) => {
        const selectedOption = e.target.options[e.target.selectedIndex];
        const selectedAlcaldia = selectedOption.innerText;
        setAlcaldia(selectedAlcaldia);
    };

    const isValidCoordinates = (coordinates) => {
        const regex = /^-?\d+\.\d+,\s*-?\d+\.\d+$/;
        return regex.test(coordinates);
    };
    
    const isValidNumero = (Numero) => {
        console.log(Numero)
        const regex = /^\d+$/;        
        if(Numero==="S/N" || Numero==="s/n"){
            return true
        }
        else{
            return regex.test(Numero);
        }
    };
    
    return (
        <div className="formulario">
            <h1>Editar dirección existente</h1>
            <form onSubmit={update}>
                <div className="form-group">
                    <label>Calle</label>
                    <input type="text" value={Calle} onChange={(e) => setCalle(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Número</label>
                    <input type="text" value={Numero} onChange={(e) => setNumero(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Alcaldía</label>
                    <select value={Alcaldia} onChange={handleAlcaldiaChange} className="form-control" required>
                        {alcaldiasCDMX.map((alcaldia, index) => (
                            <option key={index} value={alcaldia}>{alcaldia}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Código Postal</label>
                    <input type="text" value={Codigo_Postal} onChange={(e) => setCodigoPostal(e.target.value)} className="form-control" maxLength={5} required />
                </div>
                <div className="form-group">
                    <label>Coordenadas (Latitud, Longitud)</label> 
                    <input type="text" value={Coordenadas} onChange={(e) => setCoordenadas(e.target.value)} className="form-control" required /><Link to={`https://maps.google.com`} className='btn btn-success' target="_blank"><i class="fa-solid fa-map-pin"></i></Link>
                </div>
                <div className="form-group">
                    <label>Referencias</label>
                    <input type="text" value={Referencias} onChange={(e) => setReferencias(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Entre Calle 1</label>
                    <input type="text" value={Entre_Calle_1} onChange={(e) => setEntreCalle1(e.target.value)} className="form-control" required />
                </div>
                <div className="form-group">
                    <label>Entre Calle 2</label>
                    <input type="text" value={Entre_Calle_2} onChange={(e) => setEntreCalle2(e.target.value)} className="form-control" required />
                </div>
                <button type="submit" className="btn btn-primary">Actualizar dirección</button>
            </form>
        </div>
    );
}

export default CompEditDireccion;
