//archivo principal donde tenemos la aplicación
import logo from './logo.svg';
import './App.css';
//importamos componentes 
import CompShowCanchas from './cancha/ShowCanchas';
import CompCreateCancha from './cancha/CreateCancha';
import CompEditCancha from './cancha/EditCancha';
//Importamos herramientas de enrutamiento 
import { BrowserRouter, Routes, Route } from 'react-router-dom';


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
      </header>
      {/*Se utilizan 3 componentes para el enrutador, browser, routes, route. Aqui se envuelve el componente en el contexto de enrutamiento */}
      <BrowserRouter>
        <Routes>
          <Route path='/' element={<CompShowCanchas/>} />
          <Route path='/create' element={<CompCreateCancha/>} />
          <Route path='/edit/:id' element={<CompEditCancha/>} />{/*Usamos el mismo formato que definimos en el backend (routes.js) */}
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
