import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import GoogleMap from './ShowMap';
import Swal from 'sweetalert2';


const URI = 'http://localhost:8000/';

const CompShowDeportivos = () => {
    const {userType,RFC_CURP} = useParams();
    console.log(RFC_CURP) //debe tener el mismo nombre de la variable que se pasa desde el componente anterior

    console.log(userType)
    const navigate = useNavigate();
    const [deportivos, setDeportivos] = useState([]);
    useEffect(() => {
        if (RFC_CURP!="null") {
            getDeportivoInfoDeEncargado();  // Llama a la función solo si hay un RFC
        } else {        
            getDeportivoInfo(); //Si no, le muestra todo al admin
        }
    }, []);

    const getDeportivoInfo = async () => {
        const res = await axios.get(`${URI}getDeportivoInfo`);
        setDeportivos(res.data);
        console.log(res.data)
    }

    const getDeportivoInfoDeEncargado = async () => {
        console.log("rfc", RFC_CURP)
        const res = await axios.get(`${URI}getDeportivoInfoDeEncargado/${RFC_CURP}`, {
        });
        setDeportivos(res.data);
    }    

    const deleteDeportivoInfo = async (id) => {
        await axios.delete(`${URI}deleteDeportivoInfo/${id}`);
        getDeportivoInfo();
    }

    const deleteCancha = async (ID_Cancha) => {
        await axios.delete(`${URI}deleteCancha/${ID_Cancha}`);
        getDeportivoInfoDeEncargado(); //Volvemos a las canchas pertenecientes al encargado
    }

    const [expandedDeportivo, setExpandedDeportivo] = useState(null);
    const [expandedSubAccordion, setExpandedSubAccordion] = useState(null);

    //Creamos constante para esconder los divs dependiendo del tipo de usuario
    const shouldShowDiv = userType === "enc"; //Esconde funciones (div) para el admin
    const shouldHideDiv = userType === "admin"; //Esconde funciones (div) para el encargado, como modificar la direccion
    console.log("should?",shouldShowDiv)
    
    const toggleAccordion = (idDeportivo) => {
        setExpandedDeportivo(idDeportivo === expandedDeportivo ? null : idDeportivo);
    };

    const toggleSubAccordion = (index) => {
        setExpandedSubAccordion(index === expandedSubAccordion ? null : index);
    };

    const handleDeleteDeportivo = (ID_Deportivo) => {
        // Mostrar un mensaje de confirmación utilizando SweetAlert
        Swal.fire({
            title: '¿Estás seguro de eliminar este deportivo?',
            text: 'Esta acción no se puede deshacer',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminar',
            cancelButtonText: 'Cancelar',
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire(
                    '¡Eliminado!',
                    'El deportivo ha sido eliminado exitosamente.',
                    'success',                    
                );
                deleteDeportivoInfo(ID_Deportivo)            
            }
        });
    }

    const handleDelete = (ID_Cancha) => {
        // Mostrar un mensaje de confirmación utilizando SweetAlert
        Swal.fire({
            title: '¿Estás seguro de eliminar esta cancha?',
            text: 'Esta acción no se puede deshacer',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminar',
            cancelButtonText: 'Cancelar',
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire(
                    '¡Eliminado!',
                    'La cancha ha sido eliminado exitosamente.',
                    'success',                    
                );
                deleteCancha(ID_Cancha)            
            }
        });
    };

    const handleCerrarSesion = (RFC_CURP) => {
        // Mostrar un mensaje de confirmación utilizando SweetAlert
        Swal.fire({
            title: 'Cerrar Sesión',
            text: '¿Estás seguro de cerrar sesión?',
            icon: 'question',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, nos vemos',
            cancelButtonText: 'Cancelar',
        }).then((result) => {
            if (result.isConfirmed) {                
                Swal.fire(
                    '¡Hasta Luego!',
                    RFC_CURP,
                    'success',                    
                );
                navigate('/', { replace: true }); //Reemplazamos el historial
            }
        });
    }

    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => {
      setShowPassword(!showPassword);
    };


    return (
        <div className="deportivos-accordion">
            {shouldHideDiv && (
            <Link to="/createDireccion" className='btn btn-primary mt-2 mtb-2'><i class="fa-solid fa-plus"></i> Agregar nuevo deportivo</Link>
            )}
            <button to="/" onClick={handleCerrarSesion} className='btn btn-danger mt-2 mtb-2'><i class="fa fa-sign-out" aria-hidden="true"></i> Cerrar Sesión</button>
            {deportivos.map((deportivo) => {
                let lat = 0;
                let lng = 0;
                let latCancha = 0;
                let lngCancha = 0;

                if (deportivo.direccion && deportivo.direccion.Coordenadas) {
                    [lat, lng] = deportivo.direccion.Coordenadas.split(', ').map(parseFloat);
                }



                //console.log("lats1", lat, lng)
                //console.log("lats2", latCancha, lngCancha)
                
                return (
                    <div key={deportivo.ID_Deportivo} className="deportivo-card">
                        <div className="accordion-header" onClick={() => toggleAccordion(deportivo.ID_Deportivo)}>
                            <h2>{deportivo.Numero_Registro} - {deportivo.Nombre}</h2>
                            <span>{expandedDeportivo === deportivo.ID_Deportivo ? '-' : '+'}</span>
                        </div>
                        {expandedDeportivo === deportivo.ID_Deportivo && (                       
                            <div className="accordion-content">
                            <p><strong>Acepta Mascotas:</strong> {deportivo.Acepta_Mascotas ? "Sí" : "No"}</p>
                            
                            <h2>Servicios</h2>
                            <p>
                                <strong>Tienda:</strong> {deportivo.Tienda ? "Sí" : "No"} 
                                <strong> - Vestidores:</strong> {deportivo.Vestidores ? "Sí" : "No"}    
                                <strong> - Regaderas:</strong> {deportivo.Regaderas ? "Sí" : "No"}
                                <strong> - Médico:</strong> {deportivo.Médico ? "Sí" : "No"}
                            </p>
                            {shouldHideDiv && (
                                <div>
                                    <Link to ={`/editDeportivo/${deportivo.ID_Deportivo}`} className='btn btn-info'><i className="fa-regular fa-pen-to-square"></i> Editar Deportivo</Link>
                                    <button onClick={() => handleDeleteDeportivo(deportivo.ID_Deportivo)} className='btn btn-danger'><i class="fa-solid fa-trash"></i> Eliminar Deportivo</button>
                                </div>
                            )}
                                {deportivo.horario && (                                    
                                    <div className="sub-accordion">
                                        <h3 onClick={() => toggleSubAccordion(4)}>Horarios</h3>
                                        {expandedSubAccordion === 4 && (
                                            <div className="sub-accordion-content">  
                                                {shouldHideDiv && (
                                                    <div>
                                                        <Link to={`/editHorario/${deportivo.ID_Deportivo}/`} className='btn btn-primary mt-2 mtb-2'><i class="fa-regular fa-pen-to-square"></i> Editar Horario</Link>
                                                    </div>
                                                )}                                            
                                                {deportivo.horario.map((horario) => (                                                 
                                                    <div key={horario.ID_Horario} className="cancha-info">
                                                        <h2><strong>{horario.dia_semana}</strong> </h2>
                                                        {!horario.inhabil && (//Si no es inhabil, se muestran los horarios
                                                            <div>
                                                                <p><strong>Hora Apertura:</strong> {horario.hora_apertura}</p>
                                                                <p><strong>Hora Cierre:</strong> {horario.hora_cierre}</p>
                                                            </div>
                                                        ) || (//Si es inhabil, se muestra la palabra cerrado
                                                            <div>
                                                                <p><strong>Cerrado</strong></p>                                                                
                                                            </div>
                                                        )}    
                                                    </div>
                                                ))}                                                                                                                  
                                            </div>
                                        )}                                    
                                    </div>
                                )}
                                {deportivo.direccion && (
                                    <div className="sub-accordion">
                                        <h3 onClick={() => toggleSubAccordion(0)}>Dirección</h3>
                                        {expandedSubAccordion === 0 && (
                                            <div className="sub-accordion-content">
                                                <p><strong>Calle:</strong> {deportivo.direccion.Calle}</p>
                                                <p><strong>Número:</strong> {deportivo.direccion.Numero}</p>
                                                <p><strong>Alcaldía:</strong> {deportivo.direccion.Alcaldia}</p>
                                                <p><strong>Código Postal:</strong> {deportivo.direccion.Codigo_Postal}</p>
                                                <p><strong>Referencias:</strong> {deportivo.direccion.Referencias}</p>
                                                <p><strong>Entre Calle 1:</strong> {deportivo.direccion.Entre_Calle_1}</p>
                                                <p><strong>Entre Calle 2:</strong> {deportivo.direccion.Entre_Calle_2}</p>
                                                {deportivo.direccion.Coordenadas && (
                                                    <div>
                                                        {/*<p><strong>Coordenadas:</strong> {deportivo.direccion.Coordenadas}</p>*/}
                                                    
                                                        {/* 
                                                        <Link to={`https://www.google.com/maps/place/${deportivo.direccion.Coordenadas}`} className="btn btn-success" target="_blank">
                                                            <i className="fa-solid fa-map-pin"></i>
                                                        </Link>*/}
                                                        <GoogleMap coordinates={{ lat, lng }} />                                                                                                                
                                                        {shouldHideDiv && (
                                                            <div>
                                                                <Link to={`/editDireccion/${deportivo.direccion.ID_Direccion}`} className="btn btn-info">
                                                                    <i className="fa-regular fa-pen-to-square"></i> Editar Dirección
                                                                </Link>
                                                            </div>
                                                        )}                                                        
                                                    </div>
                                                )}
                                                
                                            </div>
                                        )}
                                    </div>
                                )}
                                {deportivo.encargado && (
                                    <div className="sub-accordion">
                                        <h3 onClick={() => toggleSubAccordion(1)}>Encargado</h3>
                                        {expandedSubAccordion === 1 && (
                                            <div className="sub-accordion-content">
                                                <p><strong>Nombre:</strong> {deportivo.encargado.Nombre}</p>
                                                <p><strong>Primer Apellido:</strong> {deportivo.encargado.Primer_Apellido}</p>
                                                <p><strong>Segundo Apellido:</strong> {deportivo.encargado.Segundo_Apellido}</p>
                                                <p><strong>Teléfono Fijo:</strong> {deportivo.encargado.Telefono_Fijo}</p>
                                                <p><strong>Teléfono Móvil:</strong> {deportivo.encargado.Telefono_Movil}</p>
                                                <p><strong>Cargo:</strong> {deportivo.encargado.Cargo}</p>
                                                <p><strong>RFC:</strong> {deportivo.encargado.RFC_CURP}</p>
                                                <button onClick={togglePasswordVisibility} className='btn btn-warning'>
                                                    {showPassword ? 'Ocultar' : 'Mostrar'}
                                                </button>
                                                <p>
                                                    <strong>Contraseña:</strong> {showPassword ? deportivo.encargado.Contraseña : '•'.repeat(deportivo.encargado.Contraseña.length)}
                                                </p>
                                                {shouldHideDiv && (
                                                    <div>
                                                        <Link to ={`/editEncargado/${deportivo.encargado.ID_Encargado}`} className='btn btn-info'><i className="fa-regular fa-pen-to-square"></i> Editar Encargado</Link>
                                                    </div>
                                                )}
                                            </div>
                                        )}
                                    </div>
                                )}
                                {deportivo.cancha && (
                                    <div className="sub-accordion">
                                        <h3 onClick={() => toggleSubAccordion(2)}>Canchas</h3>
                                        {expandedSubAccordion === 2 && (
                                            <div className="sub-accordion-content">
                                                {shouldShowDiv && (
                                                    <div>
                                                        <Link to={`/createCancha/${deportivo.ID_Deportivo}/${deportivo.direccion.Coordenadas}/${RFC_CURP}`} className='btn btn-primary mt-2 mtb-2'><i class="fa-solid fa-plus"></i> Crear Cancha</Link>
                                                    </div>
                                                )}
                                                {deportivo.cancha.map((cancha) => (
                                                 
                                                    <div key={cancha.ID_Cancha} className="cancha-info">
                                                        <p><strong>Tipo:</strong> {cancha.Tipo_Cancha}</p>
                                                        <p><strong>Número:</strong> {cancha.Numero_Cancha}</p>
                                                        <p><strong>Material del Piso:</strong> {cancha.Material_Piso}</p>
                                                        <p><strong>Techado:</strong> {cancha.Techado ? "Sí" : "No"}</p>
                                                        <p><strong>Con Gradas:</strong> {cancha.Con_Gradas ? "Sí" : "No"}</p>
                                                        <p><strong>Estado:</strong> {cancha.Estado}</p>
                                                        <p><strong>Capacidad de Personas:</strong> {cancha.Capacidad_Jugadores}</p>
                                                        <p><strong>Coordenadas</strong> {[latCancha, lngCancha] = cancha.Coordenadas.split(', ').map(parseFloat)} </p>                                                                                                                   
                                                        {cancha.Coordenadas && (                                                                                                                        
                                                            <div>
                                                                <GoogleMap coordinates={{ lat: latCancha, lng: lngCancha }} />

                                                                    {/* Enlaces */}                                                                
                                                                    <Link to={`https://www.google.com/maps/place/${cancha.Coordenadas}`} className="btn btn-success" target="_blank">
                                                                            <i className="fa-solid fa-map-pin"></i> Ver en Google Maps
                                                                    </Link>

                                                                {shouldShowDiv && (
                                                                    <div>                                                                    

                                                                        <Link to={`/editCancha/${cancha.ID_Cancha}/${cancha.ID_Deportivo}/${RFC_CURP}`} className='btn btn-info mt-2 mtb-2'>
                                                                            <i className="fa-regular fa-pen-to-square"></i> Editar Cancha
                                                                        </Link> 
                                                                        <button onClick={() => handleDelete(cancha.ID_Cancha)} className='btn btn-danger mt-2 mtb-2'>
                                                                            <i class="fa-solid fa-trash"></i> Eliminar Cancha
                                                                        </button>
                                                                    </div>
                                                                )}
                                                            </div>
                                                        )}
                                                    </div>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>
                        )}
                    </div>
                )
            })}
        </div>
    );
}    
export default CompShowDeportivos;
