import axios from "axios";
import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const URI = 'http://localhost:8000/createEncargado/';

const CompCreateEncargado = () => {
    const navigate = useNavigate();
    const {ID_Deportivo} = useParams(); //se guarda como un objeto, por ello los corchetes
    console.log("IDDEPORTIVO:", ID_Deportivo)
    const [Nombre, setNombre] = useState('');
    const [Primer_Apellido, setPrimerApellido] = useState('');
    const [Segundo_Apellido, setSegundoApellido] = useState('');
    const [Telefono_Fijo, setTelefonoFijo] = useState('');
    const [Telefono_Movil, setTelefonoMovil] = useState('');
    const [Cargo, setCargo] = useState('');
    const [RFC_CURP, setRFCCURP] = useState('');
    const [Contraseña, setContraseña] = useState('');
    const [formErrors, setFormErrors] = useState({}); // Estado para almacenar los errores del formulario

    // Procedimiento para actualizar
    const store = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            // en este caso pasamos la URI más la id, justo como se hizo en las pruebas con thunderclient
            await axios.post(URI, {
                Nombre: Nombre,
                ID_Deportivo: ID_Deportivo,
                Primer_Apellido: Primer_Apellido,
                Segundo_Apellido: Segundo_Apellido,
                Telefono_Fijo: Telefono_Fijo,
                Telefono_Movil: Telefono_Movil,
                Cargo: Cargo,
                RFC_CURP: RFC_CURP,
                Contraseña: Contraseña
            });
            navigate('/'); // Regresamos al principio
        }
    };

    // Función para validar el formulario
    const validateForm = () => {
        let errors = {};
        let isValid = true;

        if (!Nombre.trim()) {
            errors.Nombre = 'El nombre es requerido';
            isValid = false;
        }

        if (!Primer_Apellido.trim()) {
            errors.Primer_Apellido = 'El primer apellido es requerido';
            isValid = false;
        }

        if (!Telefono_Fijo.trim()) {
            errors.Telefono_Fijo = 'El teléfono fijo es requerido';
            isValid = false;
        }

        if (!Telefono_Movil.trim()) {
            errors.Telefono_Movil = 'El teléfono móvil es requerido';
            isValid = false;
        }

        if (!Cargo.trim()) {
            errors.Cargo = 'El cargo es requerido';
            isValid = false;
        }

        if (!RFC_CURP.trim() || RFC_CURP.length !== 16) {
            errors.RFC_CURP = 'El RFC/CURP es requerido y debe tener 16 caracteres';
            isValid = false;
        }

        if (!Contraseña.trim()) {
            errors.Contraseña = 'La contraseña es requerida';
            isValid = false;
        }

        setFormErrors(errors);
        return isValid;
    };

    return (
        <div className="formulario">
            <h1>Asignar encargado</h1>
            <form onSubmit={store}>
                <div className="form-group">
                    <label>Nombre</label>
                    <input type="text" value={Nombre} onChange={(e) => setNombre(e.target.value)} className={`form-control ${formErrors.Nombre && 'is-invalid'}`} />
                    {formErrors.Nombre && <div className="invalid-feedback">{formErrors.Nombre}</div>}
                </div>
                <div className="form-group">
                    <label>Primer Apellido</label>
                    <input type="text" value={Primer_Apellido} onChange={(e) => setPrimerApellido(e.target.value)} className={`form-control ${formErrors.Primer_Apellido && 'is-invalid'}`} />
                    {formErrors.Primer_Apellido && <div className="invalid-feedback">{formErrors.Primer_Apellido}</div>}
                </div>
                <div className="form-group">
                    <label>Segundo Apellido</label>
                    <input type="text" value={Segundo_Apellido} onChange={(e) => setSegundoApellido(e.target.value)} className={`form-control ${formErrors.Segundo_Apellido && 'is-invalid'}`} />
                </div>
                <div className="form-group">
                    <label>Teléfono Fijo</label>
                    <input type="text" value={Telefono_Fijo} onChange={(e) => setTelefonoFijo(e.target.value)} className={`form-control ${formErrors.Telefono_Fijo && 'is-invalid'}`} />
                    {formErrors.Telefono_Fijo && <div className="invalid-feedback">{formErrors.Telefono_Fijo}</div>}
                </div>
                <div className="form-group">
                    <label>Teléfono Móvil</label>
                    <input type="text" value={Telefono_Movil} onChange={(e) => setTelefonoMovil(e.target.value)} className={`form-control ${formErrors.Telefono_Movil && 'is-invalid'}`} />
                    {formErrors.Telefono_Movil && <div className="invalid-feedback">{formErrors.Telefono_Movil}</div>}
                </div>
                <div className="form-group">
                    <label>Cargo</label>
                    <input type="text" value={Cargo} onChange={(e) => setCargo(e.target.value)} className={`form-control ${formErrors.Cargo && 'is-invalid'}`} />
                    {formErrors.Cargo && <div className="invalid-feedback">{formErrors.Cargo}</div>}
                </div>
                <div className="form-group">
                    <label>RFC/CURP</label>
                    <input type="text" value={RFC_CURP} onChange={(e) => setRFCCURP(e.target.value)} className={`form-control ${formErrors.RFC_CURP && 'is-invalid'}`} />
                    {formErrors.RFC_CURP && <div className="invalid-feedback">{formErrors.RFC_CURP}</div>}
                </div>
                <div className="form-group">
                    <label>Contraseña</label>
                    <input type="password" value={Contraseña} onChange={(e) => setContraseña(e.target.value)} className={`form-control ${formErrors.Contraseña && 'is-invalid'}`} />
                    {formErrors.Contraseña && <div className="invalid-feedback">{formErrors.Contraseña}</div>}
                </div>
                <button type="submit" className="btn btn-primary">Asignar encargado</button>
            </form>
        </div>
    );
}

export default CompCreateEncargado;
