import axios from "axios";
import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"

const URI ='http://localhost:8000/canchas/'

const CompEditDireccion = () => {
    const [deportivoName, setDeportivoName] = useState('')
    const [canchaName, setCanchaName] = useState('')
    const [encargadoName, setEncargadoName] = useState('')
    const [encargadoPhone, setEncargadoPhone] = useState('')
    const [encargadoPosition, setEncargadoPosition] = useState('')
    const [encargadoRFC, setEncargadoRFC] = useState('')
    const [dirCalle, setDirCalle] = useState('')
    const [dirNumero, setDirNumero] = useState('')
    const [dirColonia, setDirColonia] = useState('')
    const [dirCP, setDirCP] = useState('')
    const [dirAlcaldia, setDirAlcaldia] = useState('')
    const [dirEntreCalles, setDirEntreCalles] = useState('')
    const [dirReferencias, setDirReferencias] = useState('')
    const [condicion, setCondicion] = useState('')
    const [coordinates, setCoordinates] = useState('')

    const navigate = useNavigate()
    const {id} = useParams() //se guarda como un objeto, por ello los corchetes

    //Procedimiento para actualizar
    const update = async (e) => {
        e.preventDefault()
        //en este caso pasamos la URI mas la id, justo como se hizo en las pruebas con thunderclient
        await axios.put(URI+id, {
            deportivoName: deportivoName, canchaName: canchaName, encargadoName: encargadoName,
                encargadoPhone: encargadoPhone, encargadoPosition: encargadoPosition, encargadoRFC: encargadoRFC,
                dirCalle: dirCalle, dirNumero: dirNumero, dirColonia: dirColonia, dirCP: dirCP, dirAlcaldia: dirAlcaldia,
                dirEntreCalles: dirEntreCalles, dirReferencias: dirReferencias, condicion:condicion, coordinates:coordinates        
            })
            navigate('/')
    }
    const getCanchaById = async () => {
        const res = await axios.get(URI+id)
        try {
            console.log("Respuesta de la api", res.data)
            setDeportivoName(res.data.deportivoName)
            console.log("CanchaName", res.data.canchaName)
            setCanchaName(res.data.canchaName)
            setEncargadoName(res.data.encargadoName)
            setEncargadoPhone(res.data.encargadoPhone)
            setEncargadoPosition(res.data.encargadoPosition)
            setEncargadoRFC(res.data.encargadoRFC)
            setDirCalle(res.data.dirCalle)
            setDirNumero(res.data.dirNumero)
            setDirColonia(res.data.dirColonia)
            setDirCP(res.data.dirCP)
            setDirAlcaldia(res.data.dirAlcaldia)
            setDirEntreCalles(res.data.dirEntreCalles)
            setDirReferencias(res.data.dirReferencias)
            setCondicion(res.data.condicion)
            setCoordinates(res.data.coordinates)
            
        } catch (error) {
            console.log("Error al obtener datos", error)
        }
        
        console.log(`DeportivoName: ${res.data.deportivoName}`)
    }

    useEffect( () => { 
        getCanchaById()
    },[])

    //Primero capturamos la informacion de la cancha desde la tabla y luego lo editamos


    return(
            <div>
                <h1>Editar cancha existente</h1>
            </div>
        )
}


export default CompEditDireccion
