/* global google */
import axios from "axios";
import React, { useState, useEffect, useRef } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const URI = "http://localhost:8080/api/canchas"; // Base URI para obtener y actualizar datos de la cancha

/* Componente para manejar el mapa */
const Map = ({ center, zoom, onMapClick, marker }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (!map && mapRef.current && window.google) {
      const newMap = new google.maps.Map(mapRef.current, {
        center,
        zoom,
      });

      newMap.addListener("click", (event) => {
        if (onMapClick) {
          onMapClick(event.latLng);
        }
      });

      setMap(newMap);
    } else if (map) {
      map.setCenter(center);
      map.setZoom(zoom);
    }
  }, [mapRef, map, center, zoom, onMapClick]);

  useEffect(() => {
    if (map && marker) {
      if (map.markers) map.markers.forEach((m) => m.setMap(null));
      const newMarker = new google.maps.Marker({
        position: marker,
        map,
      });
      map.markers = [newMarker];

      // Hacer zoom automáticamente al marcador
      map.setCenter(marker);
      map.setZoom(18); // Zoom al nivel deseado
    }
  }, [map, marker]);

  return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
};

const CompEditCancha = () => {
  const navigate = useNavigate();
  const { ID_Cancha } = useParams(); // Obtener el ID de la cancha desde la URL
  const [tipoCancha, setTipoCancha] = useState("Fútbol");
  const [numeroCancha, setNumeroCancha] = useState("");
  const [materialPiso, setMaterialPiso] = useState("Césped");
  const [techado, setTechado] = useState(false);
  const [conGradas, setConGradas] = useState(false);
  const [estado, setEstado] = useState("Buena Condición");
  const [capacidadJugadores, setCapacidadJugadores] = useState("");
  const [coordenadas, setCoordenadas] = useState("");

  const [center, setCenter] = useState({ lat: 19.430154, lng: -99.137414 });
  const [zoom, setZoom] = useState(12);
  const [marker, setMarker] = useState(null);

  const handleMapClick = (latLng) => {
    const newMarker = { lat: latLng.lat(), lng: latLng.lng() };
    setMarker(newMarker);
    setCoordenadas(`${newMarker.lat},${newMarker.lng}`); // Formato esperado por el backend
    setCenter(newMarker);
    setZoom(18);
  };

  const showErrorAlert = (message) => {
    Swal.fire({
      icon: "error",
      title: "Error",
      text: message,
      confirmButtonText: "OK",
    });
  };

  const showSuccessAlert = (message) => {
    Swal.fire({
      icon: "success",
      title: "Éxito",
      text: message,
      confirmButtonText: "OK",
    });
  };

  const updateCancha = async (e) => {
    e.preventDefault();

    if (!tipoCancha || !numeroCancha || !materialPiso || !capacidadJugadores || !coordenadas) {
      showErrorAlert("Todos los campos son obligatorios.");
      return;
    }

    try {
      const datosCancha = {
        idCancha: parseInt(ID_Cancha),
        tipoCancha,
        numeroCancha,
        materialPiso,
        techado,
        conGradas,
        estado,
        capacidadJugadores: parseInt(capacidadJugadores),
        coordenadas,
      };

      console.log("JSON enviado al backend:", JSON.stringify(datosCancha, null, 2));
      const response = await axios.put(`${URI}/updateCancha`, datosCancha, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.status === 200) {
        showSuccessAlert("La cancha fue actualizada correctamente.");
        navigate(`/showDeportivos/admin/null`);
      } else {
        showErrorAlert("No se pudo actualizar la cancha.");
      }
    } catch (error) {
      console.error("Error al actualizar la cancha:", error);
      showErrorAlert("Ocurrió un error al actualizar la cancha.");
    }
  };

  const getCanchaById = async () => {
    try {
      const res = await axios.get(`${URI}?idCancha=${ID_Cancha}`);
      const data = res.data;

      setTipoCancha(data.tipoCancha || "");
      setNumeroCancha(data.numeroCancha || "");
      setMaterialPiso(data.materialPiso || "Césped");
      setTechado(data.techado || false);
      setConGradas(data.conGradas || false);
      setEstado(data.estado || "Buena Condición");
      setCapacidadJugadores(data.capacidadJugadores || "");
      setCoordenadas(data.coordenadas || "");

      const [lat, lng] = data.coordenadas.split(",").map(parseFloat);
      const markerCoords = { lat, lng };
      setMarker(markerCoords);
      setCenter(markerCoords);
      setZoom(18); // Zoom en las coordenadas cargadas
    } catch (error) {
      console.error("Error al obtener la cancha:", error);
      showErrorAlert("No se pudo cargar la información de la cancha.");
    }
  };

  useEffect(() => {
    getCanchaById();
  }, []);

  return (
    <div className="formulario">
      <h1>Editar Cancha</h1>
      <form onSubmit={updateCancha}>
        <div className="form-group">
          <label>Coordenadas</label>
          <input type="text" value={coordenadas} disabled className="form-control" />
        </div>
        <div style={{ height: "400px", width: "100%" }}>
          <Map center={center} zoom={zoom} onMapClick={handleMapClick} marker={marker} />
        </div>
        <div className="form-group">
          <label>Tipo de Cancha</label>
          <input type="text" value={tipoCancha} onChange={(e) => setTipoCancha(e.target.value)} className="form-control" />
        </div>
        <div className="form-group">
          <label>Número de Cancha</label>
          <input type="text" value={numeroCancha} onChange={(e) => setNumeroCancha(e.target.value)} className="form-control" />
        </div>
        <div className="form-group">
          <label>Material del Piso</label>
          <input type="text" value={materialPiso} onChange={(e) => setMaterialPiso(e.target.value)} className="form-control" />
        </div>
        <div className="form-group">
          <label>Capacidad de Jugadores</label>
          <input type="text" value={capacidadJugadores} onChange={(e) => setCapacidadJugadores(e.target.value)} className="form-control" />
        </div>
        <div className="form-group">
          <label>Estado</label>
          <input type="text" value={estado} onChange={(e) => setEstado(e.target.value)} className="form-control" />
        </div>
        <div className="form-group">
          <label>Techado</label>
          <input type="checkbox" checked={techado} onChange={(e) => setTechado(e.target.checked)} />
        </div>
        <div className="form-group">
          <label>Con Gradas</label>
          <input type="checkbox" checked={conGradas} onChange={(e) => setConGradas(e.target.checked)} />
        </div>
        <button type="submit" className="btn btn-primary">Actualizar Cancha</button>
      </form>
    </div>
  );
};

export default CompEditCancha;
