import axios from "axios";
import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const URI = 'http://localhost:8000/createEncargado/';

const CompCreateEncargado = () => {
    const navigate = useNavigate();
    const {ID_Deportivo} = useParams(); //se guarda como un objeto, por ello los corchetes
    console.log("IDDEPORTIVO:", ID_Deportivo)
    const [Nombre, setNombre] = useState('');
    const [Primer_Apellido, setPrimerApellido] = useState('');
    const [Segundo_Apellido, setSegundoApellido] = useState('');
    const [Telefono_Fijo, setTelefonoFijo] = useState('');
    const [Telefono_Movil, setTelefonoMovil] = useState('');
    const [Cargo, setCargo] = useState('');
    const [RFC_CURP, setRFCCURP] = useState('');
    const [Contraseña, setContraseña] = useState('');
    
    // Estados para el manejo de errores
    const [nombreError, setNombreError] = useState('');
    const [primerApellidoError, setPrimerApellidoError] = useState('');
    const [segundoApellidoError, setSegundoApellidoError] = useState('');
    const [telefonoFijoError, setTelefonoFijoError] = useState('');
    const [telefonoMovilError, setTelefonoMovilError] = useState('');
    const [cargoError, setCargoError] = useState('');
    const [rfcCurpError, setRfcCurpError] = useState('');
    const [contraseñaError, setContraseñaError] = useState('');

    // Estados para saber si el campo ha sido tocado o no
    const [nombreTouched, setNombreTouched] = useState(false);
    const [primerApellidoTouched, setPrimerApellidoTouched] = useState(false);
    const [segundoApellidoTouched, setSegundoApellidoTouched] = useState(false);
    const [telefonoFijoTouched, setTelefonoFijoTouched] = useState(false);
    const [telefonoMovilTouched, setTelefonoMovilTouched] = useState(false);
    const [cargoTouched, setCargoTouched] = useState(false);
    const [rfcCurpTouched, setRfcCurpTouched] = useState(false);
    const [contraseñaTouched, setContraseñaTouched] = useState(false);

    // Procedimiento para actualizar
    const store = async (e) => {
        e.preventDefault();

        // Validar campos requeridos
        if (!Nombre) {
            setNombreError('Este campo es requerido');
            return;
        }
        if (!Primer_Apellido) {
            setPrimerApellidoError('Este campo es requerido');
            return;
        }
        if (!Segundo_Apellido) {
            setSegundoApellidoError('Este campo es requerido');
            return;
        }
        if (!Telefono_Fijo) {
            setTelefonoFijoError('Este campo es requerido');
            return;
        }
        if (!Telefono_Movil) {
            setTelefonoMovilError('Este campo es requerido');
            return;
        }
        if (!Cargo) {
            setCargoError('Este campo es requerido');
            return;
        }
        if (!RFC_CURP) {
            setRfcCurpError('Este campo es requerido');
            return;
        }
        if (!Contraseña) {
            setContraseñaError('Este campo es requerido');
            return;
        }

        // en este caso pasamos la URI más la id, justo como se hizo en las pruebas con thunderclient
        await axios.post(URI, {
            Nombre: Nombre,
            ID_Deportivo: ID_Deportivo,
            Primer_Apellido: Primer_Apellido,
            Segundo_Apellido: Segundo_Apellido,
            Telefono_Fijo: Telefono_Fijo,
            Telefono_Movil: Telefono_Movil,
            Cargo: Cargo,
            RFC_CURP: RFC_CURP,
            Contraseña: Contraseña
        });
        navigate('/'); // Regresamos al principio
    };

    return (
        <div className="formulario">
            <h1>Asignar encargado</h1>
            <form onSubmit={store}>
                <div className="form-group">
                    <label>Nombre</label>
                    <input type="text" value={Nombre} onChange={(e) => setNombre(e.target.value)} className="form-control" onBlur={() => setNombreTouched(true)} />
                    {nombreTouched && !Nombre && <span className="error-message">{nombreError}</span>}
                </div>
                <div className="form-group">
                    <label>Primer Apellido</label>
                    <input type="text" value={Primer_Apellido} onChange={(e) => setPrimerApellido(e.target.value)} className="form-control" onBlur={() => setPrimerApellidoTouched(true)} />
                    {primerApellidoTouched && !Primer_Apellido && <span className="error-message">{primerApellidoError}</span>}
                </div>
                <div className="form-group">
                    <label>Segundo Apellido</label>
                    <input type="text" value={Segundo_Apellido} onChange={(e) => setSegundoApellido(e.target.value)} className="form-control" onBlur={() => setSegundoApellidoTouched(true)} />
                    {segundoApellidoTouched && !Segundo_Apellido && <span className="error-message">{segundoApellidoError}</span>}
                </div>
                <div className="form-group">
                    <label>Teléfono Fijo</label>
                    <input type="text" value={Telefono_Fijo} onChange={(e) => setTelefonoFijo(e.target.value)} className="form-control" onBlur={() => setTelefonoFijoTouched(true)} />
                    {telefonoFijoTouched && !Telefono_Fijo && <span className="error-message">{telefonoFijoError}</span>}
                </div>
                <div className="form-group">
                    <label>Teléfono Móvil</label>
                    <input type="text" value={Telefono_Movil} onChange={(e) => setTelefonoMovil(e.target.value)} className="form-control" onBlur={() => setTelefonoMovilTouched(true)} />
                    {telefonoMovilTouched && !Telefono_Movil && <span className="error-message">{telefonoMovilError}</span>}
                </div>
                <div className="form-group">
                    <label>Cargo</label>
                    <input type="text" value={Cargo} onChange={(e) => setCargo(e.target.value)} className="form-control" onBlur={() => setCargoTouched(true)} />
                    {cargoTouched && !Cargo && <span className="error-message">{cargoError}</span>}
                </div>
                <div className="form-group">
                    <label>RFC/CURP</label>
                    <input type="text" value={RFC_CURP} onChange={(e) => setRFCCURP(e.target.value)} className="form-control" onBlur={() => setRfcCurpTouched(true)} />
                    {rfcCurpTouched && !RFC_CURP && <span className="error-message">{rfcCurpError}</span>}
                </div>
                <div className="form-group">
                    <label>Contraseña</label>
                    <input type="password" value={Contraseña} onChange={(e) => setContraseña(e.target.value)} className="form-control" onBlur={() => setContraseñaTouched(true)} />
                    {contraseñaTouched && !Contraseña && <span className="error-message">{contraseñaError}</span>}
                </div>
                <button type="submit" className="btn btn-primary">Asignar encargado</button>
            </form>
        </div>
    );
}
export default CompCreateEncargado;
