import axios from "axios";
import {  useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const URI = 'http://localhost:8000/createCancha';
const URICNCE = 'http://localhost:8000/getCanchaDeportivo'; //Checar Numero de Cancha Existente

const CompCreateCancha = () => {
    const navigate = useNavigate();
    const { ID_Deportivo } = useParams(); // Tomamos el id del deportivo al que pertenece
    const [Tipo_Cancha, setTipoCancha] = useState('Fútbol');
    const [Numero_Cancha, setNumeroCancha] = useState('');
    const [Material_Piso, setMaterialPiso] = useState('Césped');
    const [Techado, setTechado] = useState(false);
    const [Con_Gradas, setConGradas] = useState(false);
    const [Estado, setEstado] = useState('Buena Condición');
    const [Capacidad_Jugadores, setCapacidadJugadores] = useState('');
    const [formErrors, setFormErrors] = useState({});

    const status = ['Buena Condición', 'En Mantenimiento', 'Sin Mantenimiento'];
    const pisos = ['Césped', 'Tierra', 'Asfalto'];
    const tiposCancha = [
        'Fútbol', 
        'Fútbol 7', 
        'Fútbol Rápido', 
        'Básquetbol', 
        'Voleibol', 
        'Tenis', 
        'Paddle', 
        'Frontón', 
        'Hockey', 
        'Beisbol', 
        'Softbol',
        'Rugby',
        'Cricket',
        'Balonmano',
        'Baloncesto 3x3',
        'Bádminton',
        'Squash',
        'Pelota Vasca',
        'Pelota Mixteca'
    ];

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };     

    // Función para validar el formulario
    const validateForm = () => {
        let errors = {};
        let isValid = true;

        if (!Tipo_Cancha) {
            errors.Tipo_Cancha = "Este campo es requerido";
            isValid = false;
        }

        if (!Numero_Cancha) {
            errors.Numero_Cancha = "Este campo es requerido";
            isValid = false;
        } else if (isNaN(Number(Numero_Cancha))) {            
            if(Numero_Cancha==="S/N" || Numero_Cancha==="s/n"){
                isValid = true;
            }
            else{
                errors.Numero_Cancha = "Este campo debe ser un valor numérico";
                isValid = false;
            }
        }

        if (!Capacidad_Jugadores) {
            errors.Capacidad_Jugadores = "Este campo es requerido";
            isValid = false;
        } else if (isNaN(Number(Capacidad_Jugadores))) {
            errors.Capacidad_Jugadores = "Este campo debe ser un valor numérico";
            isValid = false;
        }

        setFormErrors(errors);
        return isValid;
    };

    // Función para verificar si ya existe una cancha con el mismo número
    const checkNumeroCanchaExistente = async () => {
        try {
            // Realizar una solicitud GET para obtener todas las canchas del deportivo
            const response = await axios.get(`${URICNCE}/${ID_Deportivo}`);
            const canchas = response.data;
            
            // Verificar si alguna cancha tiene el mismo número que la nueva cancha
            const numeroCanchaExistente = canchas.find(cancha => (
                cancha.Numero_Cancha === Numero_Cancha &&
                cancha.ID_Deportivo !== ID_Deportivo
            ));

            if(cancha.ID_Deportivo == ID_Deportivo){
                return false
            }
            // Si el número de cancha ya existe, mostrar un error
            else{// Si encuentra una dirección repetida, devuelve true
                // de lo contrario, devuelve false
                return numeroCanchaExistente !== undefined;
            }
        } catch (error) {
            console.error('Error al verificar la existencia de la cancha:', error);
        }
    };

    // Procedimiento para almacenar una nueva cancha
    const store = async (e) => {
        e.preventDefault();

        const numeroExistente = await checkNumeroCanchaExistente();
        
        if (numeroExistente) {
            showErrorAlert("Ya existe una cancha con ese numero");
            return;
        }

        // Validar el formulario antes de enviar la solicitud
        if (!validateForm()) {
            return;
        }

        try {
            // Enviar solicitud POST para crear una nueva cancha
            await axios.post(URI, {
                Tipo_Cancha,
                Numero_Cancha,
                Material_Piso,
                Techado,
                Con_Gradas,
                Estado,
                Capacidad_Jugadores,
                ID_Deportivo
            });
            navigate('/'); // Regresar al inicio después de crear la cancha
        } catch (error) {
            console.error('Error al crear la cancha:', error);
        }
    };

    return (
        <div className="formulario">
            <h1>Ingresar nueva cancha</h1>
            <form onSubmit={store}>
                <div className="form-group">
                    <label>Tipo Cancha</label>
                    <select value={Tipo_Cancha} onChange={(e) => setTipoCancha(e.target.value)} className="form-control">
                        {tiposCancha.map((tipo, index) => (
                            <option key={index} value={tipo}>{tipo}</option>
                        ))}
                    </select>                    {formErrors.Tipo_Cancha && <div className="invalid-feedback">{formErrors.Tipo_Cancha}</div>}
                </div>
                <div className="form-group">
                    <label>Numero de Cancha</label>
                    <input type="text" value={Numero_Cancha} onChange={(e) => setNumeroCancha(e.target.value)} className={`form-control ${formErrors.Numero_Cancha && 'is-invalid'}`} />
                    {formErrors.Numero_Cancha && <div className="invalid-feedback">{formErrors.Numero_Cancha}</div>}
                </div>
                <div className="form-group">
                    <label>Material del Piso</label>
                    <select value={Material_Piso} onChange={(e) => setMaterialPiso(e.target.value)} className="form-control">
                        {pisos.map((pi, index) => (
                            <option key={index} value={pi}>{pi}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Techado</label>
                    <input type="checkbox" checked={Techado} onChange={(e) => setTechado(e.target.checked)} />
                </div>
                <div className="form-group">
                    <label>Con Gradas</label>
                    <input type="checkbox" checked={Con_Gradas} onChange={(e) => setConGradas(e.target.checked)} />
                </div>
                <div className="form-group">
                    <label>Estado Actual</label>
                    <select value={Estado} onChange={(e) => setEstado(e.target.value)} className="form-control">
                        {status.map((stat, index) => (
                            <option key={index} value={stat}>{stat}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Capacidad de Personas</label>
                    <input type="text" value={Capacidad_Jugadores} onChange={(e) => setCapacidadJugadores(e.target.value)} className={`form-control ${formErrors.Capacidad_Jugadores && 'is-invalid'}`} />
                    {formErrors.Capacidad_Jugadores && <div className="invalid-feedback">{formErrors.Capacidad_Jugadores}</div>}
                </div>
                <button type="submit" className="btn btn-primary">Crear Cancha</button>
            </form>
        </div>
    );
}

export default CompCreateCancha;
