//Instalamos dependencias axios (peticiones al backend) y react-router-dom
//Instalamos bootstrap y agregamos icons del fontawesome con el cdnjs al index.html de la carpeta public
//Aqui manejamos el mostrar todas las canchas y eliminar una cancha
import axios from 'axios'
//Loss hooks useState y useEffect son para que los componentes puedan utilizar todas las caracteristicas de React
//useState retorna un valor con estado y una funcion a actualizar.
//useEffect hace referencia a algo que react no controla y lo deja ejecutarse per se
//en este caso el efecto getCanchas se va a manejar por los mecanismos que se crearon en el backend
import {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'

//Referencia al endpoint del servidor hecho con express, se encuentra en app.js en la parte de app.use('algo')
const URI ='http://localhost:8000/'


//estas son funciones con el formato de funcion flecha, aunque tambien se puededn declarar con la palabra reservada 'function'
const CompShowDeportivos = () => { 
    //configuramos hooks
    const [deportivos, setDeportivo] = useState([])
    useEffect( () => { 
        getDeportivoInfo()
    },[])
    //Procedimiento para mostrar todas las canchas
    const getDeportivoInfo = async () => {
        const res = await axios.get(`${URI}${'getDeportivoInfo'}`)
        setDeportivo(res.data)
        console.log(res.data)
    }

    const deleteDeportivo = async (id) => {
        await axios.delete(`${URI}${'deleteDeportivo'}${'ID_Deportivo'}`)
        getDeportivoInfo() //para que muestre los resultados despues de la eliminacion
    }

    const [expandedDeportivo, setExpandedDeportivo] = useState(null);
    const [expandedSubAccordion, setExpandedSubAccordion] = useState(null);
  
    const toggleAccordion = (idDeportivo) => {
      setExpandedDeportivo(idDeportivo === expandedDeportivo ? null : idDeportivo);
    };
  
    const toggleSubAccordion = (index) => {
      setExpandedSubAccordion(index === expandedSubAccordion ? null : index);
    };

    //retornamos una vista (tabla de bootstrap)
    return (
        <div className="deportivos-accordion">
          {deportivos.map((deportivo) => (
            <div key={deportivo.ID_Deportivo} className="deportivo-card">
              <div className="accordion-header" onClick={() => toggleAccordion(deportivo.ID_Deportivo)}>
                <h2>{deportivo.Nombre}</h2>
                <span>{expandedDeportivo === deportivo.ID_Deportivo ? '-' : '+'}</span>
              </div>
              {expandedDeportivo === deportivo.ID_Deportivo && (
                <div className="accordion-content">
                  {deportivo.direccion && (
                    <div className="sub-accordion">
                      <h3 onClick={() => toggleSubAccordion(0)}>Dirección</h3>
                      {expandedSubAccordion === 0 && (
                        <div className="sub-accordion-content">
                          <p>{deportivo.direccion.Calle} {deportivo.direccion.Numero}, {deportivo.direccion.Alcaldia}, {deportivo.direccion.Codigo_Postal}</p>
                          <p>{deportivo.direccion.Referencias} {deportivo.direccion.Entre_Calle_1}, {deportivo.direccion.Entre_Calle_2}</p>
                        </div>
                      )}
                    </div>
                  )}
                  {deportivo.encargado && (
                    <div className="sub-accordion">
                      <h3 onClick={() => toggleSubAccordion(1)}>Encargado</h3>
                      {expandedSubAccordion === 1 && (
                        <div className="sub-accordion-content">
                          <p>{deportivo.encargado.Nombre} {deportivo.encargado.Primer_Apellido} {deportivo.encargado.Segundo_Apellido}</p>
                          <p>Teléfono Fijo: {deportivo.encargado.Telefono_Fijo}</p>
                          <p>Teléfono Móvil: {deportivo.encargado.Telefono_Movil}</p>
                          <p>Cargo: {deportivo.encargado.Cargo}</p>
                          <p>RFC/CURP: {deportivo.encargado.RFC_CURP}</p>
                        </div>
                      )}
                    </div>
                  )}
                  {deportivo.cancha && (
                    <div className="sub-accordion">
                      <h3 onClick={() => toggleSubAccordion(2)}>Canchas</h3>
                      {expandedSubAccordion === 2 && (
                        <div className="sub-accordion-content">
                          {deportivo.cancha.map((cancha) => (
                            <div key={cancha.ID_Cancha} className="cancha-info">
                              <p><strong>Tipo:</strong> {cancha.Tipo_Cancha}</p>
                              <p><strong>Número:</strong> {cancha.Numero_Cancha}</p>
                              <p><strong>Material del Piso:</strong> {cancha.Material_Piso}</p>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      );
    }
    
export default CompShowDeportivos
