/* global google */
import axios from "axios";
import {  useState, useEffect, useRef  } from "react";
import { Wrapper } from "@googlemaps/react-wrapper";
import { useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const API_KEY = "AIzaSyCiSwQ835LzONcwinoIhq1zE-7keqrGKSs"; 

const URI = 'http://localhost:8000/oneCancha/';
const updURI = 'http://localhost:8000/updateCancha/';
const URICNCE = 'http://localhost:8000/getCanchaDeportivo'; //Checar Numero de Cancha Existente

/* Inicio Funcionamiento de Maps */
// Componente para manejar el mapa
const Map = ({ center, zoom, onMapClick, marker }) => {
    const mapRef = useRef(null);
    const [map, setMap] = useState(null);
  
    useEffect(() => {
      if (!map && mapRef.current) {
        // Crear un nuevo mapa
        const newMap = new google.maps.Map(mapRef.current, {
          center,
          zoom,
        });
  
        // Agregar el manejador de clic en el mapa
        newMap.addListener("click", (event) => {
          if (onMapClick) {
            onMapClick(event.latLng);
          }
        });
  
        setMap(newMap);
      } else if (map) {
        // Actualizar el centro y el nivel de zoom del mapa existente
        map.setCenter(center);
        console.log(center.lat)
        map.setZoom(zoom);
    }
    }, [mapRef, map, center, zoom, onMapClick]);
  
    useEffect(() => {
      if (map && marker) {
        // Eliminar marcadores existentes
        map.markers && map.markers.forEach((m) => m.setMap(null));
        
        // Crear nuevo marcador
        const newMarker = new google.maps.Marker({
          position: marker,
          map,
        });
        
        map.markers = [newMarker];
      }
    }, [map, marker]);
  
    return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
  };
/* Fin Funcionamiento de Maps */

const CompEditCancha = () => {
    const navigate = useNavigate();
    const { ID_Cancha, ID_Deportivo } = useParams(); // Obtenemos 2 parametros, idcancha para recuperar y editar los datos de la cancha, y iddeportivo para buscar otras canchas con el mismo valor
    const [Tipo_Cancha, setTipoCancha] = useState('Fútbol');
    const [Numero_Cancha, setNumeroCancha] = useState('');
    const [Material_Piso, setMaterialPiso] = useState('Césped');
    const [Techado, setTechado] = useState(false);
    const [Con_Gradas, setConGradas] = useState(false);
    const [Estado, setEstado] = useState('Buena Condición');
    const [Capacidad_Jugadores, setCapacidadJugadores] = useState('');
    const [formErrors, setFormErrors] = useState({});
    const [Coordenadas, setCoordenadas] = useState('');
    //Para el mapa
    const [center, setCenter] = useState({ lat: 19.430154, lng: -99.137414 });
    const [zoom, setZoom] = useState(17);
    const [marker, setMarker] = useState(null);
    const searchInputRef = useRef(null);

    const handleMapClick = (latLng) => {
        const newMarker = { lat: latLng.lat(), lng: latLng.lng() };
        setMarker(newMarker);
        setCoordenadas(`${newMarker.lat}, ${newMarker.lng}`);
        setCenter(`${newMarker.lat}, ${newMarker.lng}`);
      };


      useEffect(() => {
        // Configurar autocompletar en el campo de búsqueda
        if (searchInputRef.current) {
          const autocomplete = new google.maps.places.Autocomplete(searchInputRef.current);
    
          // Limitar los tipos de búsqueda a establecimientos (lugares)
          autocomplete.setTypes(["establishment"]);
    
          // Agregar filtro de palabras clave para solo buscar deportivos
          autocomplete.setFields(["name", "geometry", "types"]);
          
    
          // Define los límites de la Ciudad de México
          const mexicoCityBounds = new google.maps.LatLngBounds(
            new google.maps.LatLng(19.1500, -99.3250), // Suroeste de la Ciudad de México
            new google.maps.LatLng(19.6300, -98.9900) // Noreste de la Ciudad de México
          );
    
          // Establece los límites geográficos para restringir la búsqueda a la Ciudad de México
          autocomplete.setBounds(mexicoCityBounds);
    
          // Manejador cuando se selecciona un lugar
          autocomplete.addListener("place_changed", () => {
            const place = autocomplete.getPlace();
            
            if (place && place.geometry) {
              const location = place.geometry.location;
              const newCenter = {
                lat: location.lat(),
                lng: location.lng(),
              };
    
              // Centrar el mapa en la ubicación seleccionada y agregar un marcador
              setCenter(newCenter);
              //console.log("here",newCenter.lat)
              setMarker(newCenter);
              setCoordenadas(`${newCenter.lat}, ${newCenter.lng}`);
            }
          });
        }
      }, [searchInputRef]);



    const status = [
        'Buena Condicion',
        'En Mantenimiento',
        'Sin Mantenimiento'
      ];
    const pisos = [
        'Césped',
        'Tierra',
        'Asfalto'
      ];
      const tiposCancha = [
        'Fútbol', 
        'Fútbol 7', 
        'Fútbol Rápido', 
        'Básquetbol', 
        'Voleibol', 
        'Tenis', 
        'Paddle', 
        'Frontón', 
        'Hockey', 
        'Beisbol', 
        'Softbol',
        'Rugby',
        'Cricket',
        'Balonmano',
        'Baloncesto 3x3',
        'Bádminton',
        'Squash',
        'Pelota Vasca',
        'Pelota Mixteca'
    ];

    const showErrorAlert = (message) => {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: message,
            confirmButtonText: 'OK'
        });
    };      

    const validateForm = () => {
        let errors = {};
        let isValid = true;

        if (!Tipo_Cancha) {
            errors.Tipo_Cancha = "Este campo es requerido";
            isValid = false;
        }

        if (!Numero_Cancha) {
            errors.Numero_Cancha = "Este campo es requerido";
            isValid = false;
        } else if (isNaN(Number(Numero_Cancha))) {            
            if(Numero_Cancha=="S/N" || Numero_Cancha=="s/n"){
                isValid = true;
            }
            else{
                errors.Numero_Cancha = "Este campo debe ser un valor numérico o S/N";
                isValid = false;
            }
        }

        if (!Capacidad_Jugadores) {
            errors.Capacidad_Jugadores = "Este campo es requerido";
            isValid = false;
        } else if (isNaN(Number(Capacidad_Jugadores))) {
            errors.Capacidad_Jugadores = "Este campo debe ser un valor numérico";
            isValid = false;
        }

        setFormErrors(errors);
        return isValid;
    };

    useEffect(() => {
        getCanchaById();
    }, []);

    const getCanchaById = async () => {
        const res = await axios.get(URI+ID_Cancha);
        try {
            setTipoCancha(res.data.Tipo_Cancha || '');
            setNumeroCancha(res.data.Numero_Cancha || '');
            setMaterialPiso(res.data.Material_Piso || '');
            setTechado(res.data.Techado || false);
            setConGradas(res.data.Con_Gradas || false);
            setEstado(res.data.Estado || '');
            setCapacidadJugadores(res.data.Capacidad_Jugadores || ''); 
            setCoordenadas(res.data.Coordenadas || ''); 
            //console.log("data", res.data)                      
            //console.log("coords", Coordenadas) 
            const [lat, lng] = res.data.Coordenadas.split(', ').map(parseFloat);
            const coordenadas = { lat, lng };

            setCoordenadas(res.data.Coordenadas || '');
            // Establece el estado del marcador con las coordenadas recuperadas
            setMarker(coordenadas);
        
            // Centrar el mapa en la posición del marcador
            setCenter(coordenadas);                  
        } catch (error) {
            console.log("Error al obtener datos", error);
        }
    };

    // Función para verificar si ya existe una cancha con el mismo número
    const checkNumeroCanchaExistente = async () => {
        try {    
            //console.log("iddeportivo", ID_Deportivo)        
            // Realizar una solicitud GET para obtener todas las canchas del deportivo
            const response = await axios.get(`${URICNCE}/${ID_Deportivo}`);
            const canchas = response.data;
            //console.log("datos", response.data)        

            // Verificar si alguna cancha tiene el mismo número que la nueva cancha
            const numeroCanchaExistente = canchas.find(cancha => (
                cancha.Numero_Cancha === Numero_Cancha &&
                cancha.ID_Deportivo !== ID_Deportivo            
            ));
            
            //Exceptuamos a la misma cancha que estamos editando.
            console.log("ID del deportivo repetido, ID deportivo actual", parseInt(numeroCanchaExistente.ID_Deportivo), parseInt(ID_Deportivo))      
            if(parseInt(numeroCanchaExistente.ID_Cancha) == parseInt(ID_Cancha)){
                //showErrorAlert("Exento");
                return false;
            }
            else if(Numero_Cancha === "S/N" || Numero_Cancha === "s/n"){
                //console.log("SN")
                return false;
            }
            else{
                return numeroCanchaExistente !== undefined;
            }
            
        } catch (error) {
            console.error('Error al verificar la existencia de la cancha:', error);
        }
    };    

    const update = async (e) => {
        e.preventDefault();

        const numeroExistente = await checkNumeroCanchaExistente();
        
        // Validar coordenadas
        if (!isValidCoordinates(Coordenadas)) {
            showErrorAlert("Las coordenadas no tienen el formato correcto");
            return;
        }

        if (numeroExistente) {
            showErrorAlert("Ya existe una cancha con ese número");            
            return;
        }

        else if (validateForm()) {
            await axios.put(updURI+ID_Cancha, {
                Tipo_Cancha: Tipo_Cancha,
                Numero_Cancha: Numero_Cancha,
                Material_Piso: Material_Piso,
                Techado: Techado,
                Con_Gradas: Con_Gradas,
                Estado: Estado,
                Capacidad_Jugadores: Capacidad_Jugadores,
                Coordenadas: Coordenadas
            });
            navigate('/'); // Regresamos al principio
        }
    };

    const isValidCoordinates = (Coordenadas) => {
        const regex = /^-?\d+\.\d+,\s*-?\d+\.\d+$/;
        return regex.test(Coordenadas);
    };

    return (
        <div className="formulario">
            <h1>Editar Cancha</h1>
            <form onSubmit={update}>
            <div>
                <label><strong>Coordenadas (Latitud, Longitud)</strong></label> 
                <input type="text" value={Coordenadas} onChange={(e) => setCoordenadas(e.target.value)} className="form-control" required disabled/>
            </div>    
            <div className="form-group">
                <div style={{ height: "100vh", width: "100%" }}>
                    <Wrapper apiKey={API_KEY} render={(status) => <h1>{status}</h1>}>
                        <Map center={center} zoom={zoom} onMapClick={handleMapClick} marker={marker} />
                    </Wrapper>
                </div>
            </div>
                <div className="form-group" style={{ padding: "20px" }}>
                    <label>Tipo de Cancha</label>
                    <select value={Tipo_Cancha} onChange={(e) => setTipoCancha(e.target.value)} className="form-control">
                        {tiposCancha.map((tipo, index) => (
                            <option key={index} value={tipo}>{tipo}</option>
                        ))}
                    </select>                    {formErrors.Tipo_Cancha && <div className="invalid-feedback">{formErrors.Tipo_Cancha}</div>}
                </div>
                <div className="form-group">
                    <label>Numero de Cancha</label>
                    <input type="text" value={Numero_Cancha} onChange={(e) => setNumeroCancha(e.target.value)} className={`form-control ${formErrors.Numero_Cancha && 'is-invalid'}`} />
                    {formErrors.Numero_Cancha && <div className="invalid-feedback">{formErrors.Numero_Cancha}</div>}
                </div>
                <div className="form-group">
                    <label>Material del Piso</label>
                    <select value={Material_Piso} onChange={(e) => setMaterialPiso(e.target.value)} className="form-control">
                        {pisos.map((pi, index) => (
                            <option key={index} value={pi}>{pi}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Techado</label>
                    <input type="checkbox" checked={Techado} onChange={(e) => setTechado(e.target.checked)} />
                </div>
                <div className="form-group">
                    <label>Con Gradas</label>
                    <input type="checkbox" checked={Con_Gradas} onChange={(e) => setConGradas(e.target.checked)} />
                </div>
                <div className="form-group">
                    <label>Estado Actual</label>
                    <select value={Estado} onChange={(e) => setEstado(e.target.value)} className="form-control">
                        {status.map((stat, index) => (
                            <option key={index} value={stat}>{stat}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Capacidad de Personas</label>
                    <input type="text" value={Capacidad_Jugadores} onChange={(e) => setCapacidadJugadores(e.target.value)} className={`form-control ${formErrors.Capacidad_Jugadores && 'is-invalid'}`} />
                    {formErrors.Capacidad_Jugadores && <div className="invalid-feedback">{formErrors.Capacidad_Jugadores}</div>}
                </div>
                <button type="submit" className="btn btn-primary">Editar Cancha</button>
            </form>
        </div>
    );
}

export default CompEditCancha;
